import React, { useEffect, useRef, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { StateProvider } from './context/StateContext';
import { Helmet } from 'react-helmet';
import {
  FaDatabase,
  FaTable,
  FaProjectDiagram,
  FaCode,
  FaCommentAlt,
  FaStream
} from 'react-icons/fa';

import ActionPanel from './components/ActionPanel';
import OutputPanel from './components/OutputPanel';
import CodePanel from './components/CodePanel';
import MessagePanel from './components/messagePanel';
import NormalizedTableView from './components/TableView';
import ERDiagramPage from './components/ERDiagramPage';
import FDPage from './components/FDPage';

import './style.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

const PanelContainer = ({ children, title, icon }) => (
  <div className="panel-base">
    <div className="panel-header">
      <h3 className="panel-title">
        {icon && React.cloneElement(icon, { className: 'panel-icon' })}
        {title}
      </h3>
    </div>
    <div className="panel-content">{children}</div>
  </div>
);

const MainLayout = () => {
  // Refs for panels
  const leftRef = useRef(null);
  const centerRef = useRef(null);
  const rightRef = useRef(null);
  const messagesRef = useRef(null);
  const codeRef = useRef(null);

  // States for horizontal widths
  const [leftWidth, setLeftWidth] = useState(() => {
    const saved = localStorage.getItem('leftWidth');
    return saved ? parseInt(saved, 10) : 240;
  });
  const [centerWidth, setCenterWidth] = useState(() => {
    const saved = localStorage.getItem('centerWidth');
    return saved ? parseInt(saved, 10) : 600; // default center width fallback
  });
  const [rightWidth, setRightWidth] = useState(() => {
    const saved = localStorage.getItem('rightWidth');
    return saved ? parseInt(saved, 10) : 240;
  });

  // States for vertical heights inside right panel
  const [messagesHeight, setMessagesHeight] = useState(() => {
    const saved = localStorage.getItem('messagesHeight');
    return saved ? parseInt(saved, 10) : 150;
  });
  const [codeHeight, setCodeHeight] = useState(() => {
    const saved = localStorage.getItem('codeHeight');
    return saved ? parseInt(saved, 10) : 240;
  });

  useEffect(() => {
    // ----- Horizontal resizing (left <-> center and center <-> right) -----
    const leftCenterHandle = document.querySelector('.resize-handle-x.left-center');
    const centerRightHandle = document.querySelector('.resize-handle-x.center-right');

    const minLeftWidth = 150;
    const minCenterWidth = 200;
    const minRightWidth = 200;

    function setupHorizontalResize(handle, leftEl, rightEl, minLeft, minRight, setLeftWidthState, setRightWidthState) {
      let startX = 0;
      let startLeftWidth = 0;
      let startRightWidth = 0;

      function onMouseMove(e) {
        let dx = e.clientX - startX;
        let newLeftWidth = startLeftWidth + dx;
        let newRightWidth = startRightWidth - dx;

        // Clamp widths
        if (newLeftWidth < minLeft) {
          newLeftWidth = minLeft;
          newRightWidth = startLeftWidth + startRightWidth - minLeft;
        } else if (newRightWidth < minRight) {
          newRightWidth = minRight;
          newLeftWidth = startLeftWidth + startRightWidth - minRight;
        }

        leftEl.style.flex = `0 0 ${newLeftWidth}px`;
        rightEl.style.flex = `0 0 ${newRightWidth}px`;
      }

      function onMouseUp() {
        document.removeEventListener('mousemove', onMouseMove);
        document.removeEventListener('mouseup', onMouseUp);

        // Save sizes in state and localStorage
        if (leftRef.current && rightRef.current) {
          const leftWidthPx = leftRef.current.getBoundingClientRect().width;
          const rightWidthPx = rightRef.current.getBoundingClientRect().width;

          setLeftWidthState(Math.round(leftWidthPx));
          setRightWidthState(Math.round(rightWidthPx));

          // Save to localStorage
          if (setLeftWidthState === setLeftWidth) {
            localStorage.setItem('leftWidth', Math.round(leftWidthPx));
            localStorage.setItem('centerWidth', Math.round(rightWidthPx));
          } else {
            // For center-right handle
            localStorage.setItem('centerWidth', Math.round(leftWidthPx));
            localStorage.setItem('rightWidth', Math.round(rightWidthPx));
          }
        }
      }

      function onMouseDown(e) {
        e.preventDefault();
        startX = e.clientX;
        startLeftWidth = leftEl.getBoundingClientRect().width;
        startRightWidth = rightEl.getBoundingClientRect().width;

        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
      }

      handle.addEventListener('mousedown', onMouseDown);

      return () => {
        handle.removeEventListener('mousedown', onMouseDown);
      };
    }

    // ----- Vertical resizing inside right panel (messages <-> code) -----
    const verticalHandle = document.querySelector('.resize-handle-y.messages-code');

    const minMessagesHeight = 80;
    const minCodeHeight = 80;

    function setupVerticalResize(handle, topEl, bottomEl, minTop, minBottom) {
      let startY = 0;
      let startTopHeight = 0;
      let startBottomHeight = 0;

      function onMouseMove(e) {
        let dy = e.clientY - startY;
        let newTopHeight = startTopHeight + dy;
        let newBottomHeight = startBottomHeight - dy;

        // Clamp heights
        if (newTopHeight < minTop) {
          newTopHeight = minTop;
          newBottomHeight = startTopHeight + startBottomHeight - minTop;
        } else if (newBottomHeight < minBottom) {
          newBottomHeight = minBottom;
          newTopHeight = startTopHeight + startBottomHeight - minBottom;
        }

        topEl.style.flex = `0 0 ${newTopHeight}px`;
        bottomEl.style.flex = `0 0 ${newBottomHeight}px`;
      }

      function onMouseUp() {
        if (topEl && bottomEl) {
          const topHeightPx = topEl.getBoundingClientRect().height;
          const bottomHeightPx = bottomEl.getBoundingClientRect().height;

          setMessagesHeight(Math.round(topHeightPx));
          setCodeHeight(Math.round(bottomHeightPx));

          localStorage.setItem('messagesHeight', Math.round(topHeightPx));
          localStorage.setItem('codeHeight', Math.round(bottomHeightPx));
        }

        document.removeEventListener('mousemove', onMouseMove);
        document.removeEventListener('mouseup', onMouseUp);
      }

      function onMouseDown(e) {
        e.preventDefault();
        startY = e.clientY;
        startTopHeight = topEl.getBoundingClientRect().height;
        startBottomHeight = bottomEl.getBoundingClientRect().height;

        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
      }

      handle.addEventListener('mousedown', onMouseDown);

      return () => {
        handle.removeEventListener('mousedown', onMouseDown);
      };
    }

    const cleanups = [];
    if (leftCenterHandle && leftRef.current && centerRef.current) {
      cleanups.push(setupHorizontalResize(leftCenterHandle, leftRef.current, centerRef.current, minLeftWidth, minCenterWidth, setLeftWidth, setCenterWidth));
    }
    if (centerRightHandle && centerRef.current && rightRef.current) {
      cleanups.push(setupHorizontalResize(centerRightHandle, centerRef.current, rightRef.current, minCenterWidth, minRightWidth, setCenterWidth, setRightWidth));
    }
    if (verticalHandle && messagesRef.current && codeRef.current) {
      cleanups.push(setupVerticalResize(verticalHandle, messagesRef.current, codeRef.current, minMessagesHeight, minCodeHeight));
    }

    return () => {
      cleanups.forEach((cleanup) => cleanup && cleanup());
    };
  }, []);

  // Apply stored widths and heights on mount and when they change
  useEffect(() => {
    if (leftRef.current) {
      leftRef.current.style.flex = `0 0 ${leftWidth}px`;
    }
    if (centerRef.current) {
      centerRef.current.style.flex = `0 0 ${centerWidth}px`;
    }
    if (rightRef.current) {
      rightRef.current.style.flex = `0 0 ${rightWidth}px`;
    }
  }, [leftWidth, centerWidth, rightWidth]);

  useEffect(() => {
    if (messagesRef.current) {
      messagesRef.current.style.flex = `0 0 ${messagesHeight}px`;
    }
    if (codeRef.current) {
      codeRef.current.style.flex = `1 1 auto`; // keep flexible for code panel vertical grow
    }
  }, [messagesHeight]);

  // For code panel height, we set flex-basis to remaining height; so no fixed height needed,
  // but if you want fixed you can use `codeHeight` similarly.

  return (
    <div className="app-container">
      {/* Left panel */}
      <div className="left-panel" ref={leftRef} style={{ flex: `0 0 ${leftWidth}px` }}>
        <PanelContainer title="Actions" icon={<FaDatabase />}>
          <ActionPanel />
        </PanelContainer>
      </div>

      {/* Horizontal resize handle between left and center */}
      <div className="resize-handle-x left-center" />

      {/* Center panel */}
      <div className="center-panel" ref={centerRef} style={{ flex: `0 0 ${centerWidth}px` }}>
        <PanelContainer title="Database Output" icon={<FaTable />}>
          <OutputPanel />
        </PanelContainer>
      </div>

      {/* Horizontal resize handle between center and right */}
      <div className="resize-handle-x center-right" />

      {/* Right panel */}
      <div
        className="right-panel"
        ref={rightRef}
        style={{ flex: `0 0 ${rightWidth}px`, display: 'flex', flexDirection: 'column' }}
      >
        {/* Messages panel with vertical resize */}
        <div
          ref={messagesRef}
          style={{ flex: `0 0 ${messagesHeight}px`, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}
        >
          <PanelContainer title="Messages" icon={<FaCommentAlt />}>
            <MessagePanel />
          </PanelContainer>
        </div>

        {/* Vertical resize handle between messages and code */}
        <div className="resize-handle-y messages-code" />

        {/* Code panel */}
        <div
          ref={codeRef}
          style={{ flex: `1 1 auto`, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}
        >
          <PanelContainer title="Generated Code" icon={<FaCode />}>
            <CodePanel />
          </PanelContainer>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <StateProvider>
      <Helmet>
        <title>Database Design Studio</title>
        <meta
          name="description"
          content="Tool for creating database designs and ER diagrams"
        />
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="true" />
        <link
          href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&family=Fira+Code:wght@400;500&display=swap"
          rel="stylesheet"
        />
      </Helmet>

      <Router>
        <Routes>
          <Route path="/" element={<MainLayout />} />
          <Route
            path="/table-view"
            element={
              <PanelContainer title="Normalized Tables" icon={<FaTable />}>
                <NormalizedTableView />
              </PanelContainer>
            }
          />
          <Route
            path="/er-diagram"
            element={
              <PanelContainer title="ER Diagram" icon={<FaProjectDiagram />}>
                <ERDiagramPage />
              </PanelContainer>
            }
          />
          <Route
            path="/fd-view"
            element={
              <PanelContainer title="Detected Functional Dependencies" icon={<FaStream />}>
                <FDPage />
              </PanelContainer>
            }
          />
        </Routes>
      </Router>

      {/* SVG Arrowhead Definitions */}
      <svg className="svg-defs">
        <defs>
          <marker
            id="arrowhead"
            markerWidth="10"
            markerHeight="7"
            refX="9"
            refY="3.5"
            orient="auto"
          >
            <polygon points="0 0, 10 3.5, 0 7" fill="#1a73e8" />
          </marker>
        </defs>
      </svg>
    </StateProvider>
  );
};

export default App;
