import React, { useRef } from 'react';
import { uploadFile, triggerBackendStep, checkDependencyPreservation } from '../api';
import { useStateContext } from '../context/StateContext';

const ActionPanel = () => {
  const { addMessage, addWorkflowStep, fetchNormalizedTables, originalFDs, decomposedSchemas } = useStateContext();
  const fileInputRef = useRef(null);

  // Handle file upload using uploadFile API
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    try {
      const message = await uploadFile(file);
      addMessage(message);
      addWorkflowStep('Upload');
    } catch (error) {
      addMessage(error.message);
    }
  };

  // Generic handler for backend steps using triggerBackendStep API
  const handleBackendAction = async (stepName) => {
    try {
      const message = await triggerBackendStep(stepName);
      addMessage(message);
      addWorkflowStep(stepName);


      if (stepName === 'FDModified') {
        await fetchOriginalFDs();
      }


    // After Normalizing Tables, refresh the tables list
      if (stepName === 'NormalizeTable') {
        await fetchNormalizedTables();  // <-- This triggers fetching tables from backend
      }

    } catch (error) {
      addMessage(error.message);
    }
  };





  const handleDependencyPreservation = async () => {
    try {
      // Validate data availability
      if (originalFDs.length === 0 || decomposedSchemas.length === 0) {
        addMessage('Error: Functional Dependencies or Decomposed Schemas are missing.');
        return;
      }

      // Call backend API with actual data
      const message = await checkDependencyPreservation(originalFDs, decomposedSchemas);
      addMessage(message);
      addWorkflowStep('DependencyPreservation');
    } catch (error) {
      addMessage(error.message);
    }
  };


  return (
    <div className="action-panel">
      <h2>Action Panel</h2>

      {/* File Upload Section */}
      <div>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileUpload}
          style={{ display: 'none' }}
        />
        <button onClick={() => fileInputRef.current.click()}>Upload File</button>
      </div>

      {/* Action Buttons */}
      <div className="actions">
        <button onClick={() => handleBackendAction('ConvertToCSV')}>
          Convert to CSV
        </button>
        <button onClick={() => handleBackendAction('CleanModify')}>
          Clean & Modify
        </button>
        <button onClick={() => handleBackendAction('FDModified')}>
          Detect Functional Dependencies
        </button>
        <button onClick={() => handleBackendAction('NormalizeTable')}>
          Normalize Tables
        </button>
        <button onClick={() => handleBackendAction('KeyDetection')}>
          Detect Keys
        </button>
        <button onClick={handleDependencyPreservation}>
          Check Dependency Preservation
        </button>
        <button onClick={() => handleBackendAction('LosslessCheck')}>
          Check Lossless Decomposition
        </button>
        <button onClick={() => handleBackendAction('ERDiagram')}>
          Generate ER Diagram
        </button>
      </div>
    </div>
  );
};

export default ActionPanel;
