import React from "react";
import { useStateContext } from "../context/StateContext";

const MessagePanel = () => {
  const {
    message,
    normalizedTables,
    selectedTable,
    setSelectedTable,
  } = useStateContext();

  return (
    <div>
      <h3>Messages & Tables</h3>

      {/* Show dropdown if tables are available */}
      {normalizedTables && normalizedTables.length > 0 ? (
        <div style={{ marginBottom: "15px" }}>
          <label htmlFor="table-select" style={{ marginRight: "10px" }}>
            Select Table:
          </label>
          <select
            id="table-select"
            value={selectedTable || ""}
            onChange={(e) => setSelectedTable(e.target.value)}
            style={{ padding: "5px", borderRadius: "5px" }}
          >
            <option value="" disabled>
              -- Choose a table --
            </option>
            {normalizedTables.map((table) => (
              <option key={table} value={table}>
                {table}
              </option>
            ))}
          </select>
        </div>
      ) : null}

      {/* Show message area */}
      <div
        style={{
          border: "1px solid #ccc",
          backgroundColor: "#fafafa",
          padding: "10px",
          borderRadius: "6px",
          minHeight: "200px",
          whiteSpace: "pre-wrap",
          fontFamily: "Arial, sans-serif",
          fontSize: "14px",
          color: message && message.toLowerCase().includes("error") ? "#d32f2f" : "#333",
        }}
      >
        {message || "Messages will appear here..."}
      </div>
    </div>
  );
};

export default MessagePanel;
