import React from 'react';
import { useStateContext } from '../context/StateContext';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { coy } from 'react-syntax-highlighter/dist/esm/styles/prism';

const CodePanel = () => {
  const { currentCode } = useStateContext();

  return (
    <div className="code-panel" style={{ padding: '10px', border: '1px solid #ccc', height: '100%', overflowY: 'auto', background: '#f0f0f0' }}>
      <h2>Code Panel</h2>
      {currentCode ? (
        <SyntaxHighlighter language="python" style={coy} customStyle={{ borderRadius: '10px', padding: '20px', background: '#fff' }}>
          {currentCode}
        </SyntaxHighlighter>
      ) : (
        <p>No code selected. Click a step in Output Panel.</p>
      )}
    </div>
  );
};

export default CodePanel;
