import React from "react";
import { useStateContext } from "../context/StateContext";

const MessagePanel = () => {
  const {
    messages,
    normalizedTables,
    selectedTableHTML,
    selectTable,
  } = useStateContext();

  const handleTableChange = (e) => {
    selectTable(e.target.value);
  };

  return (
    <div>
      <h3>Messages & Tables</h3>

      {normalizedTables && normalizedTables.length > 0 ? (
        <div style={{ marginBottom: "15px" }}>
          <label htmlFor="table-select" style={{ marginRight: "10px" }}>
            Select Table:
          </label>
          <select
            id="table-select"
            onChange={handleTableChange}
            style={{ padding: "5px", borderRadius: "5px" }}
          >
            <option value="" disabled selected>
              -- Choose a table --
            </option>
            {normalizedTables.map((table) => (
              <option key={table} value={table}>
                {table}
              </option>
            ))}
          </select>
        </div>
      ) : null}

      <div
        style={{
          border: "1px solid #ccc",
          backgroundColor: "#fafafa",
          padding: "10px",
          borderRadius: "6px",
          minHeight: "150px",
          whiteSpace: "pre-wrap",
          fontFamily: "Arial, sans-serif",
          fontSize: "14px",
        }}
      >
        {messages.length > 0
          ? messages.map((msg, idx) => <div key={idx}>{msg}</div>)
          : "Messages will appear here..."}
      </div>

      <div
        style={{
          border: "1px solid #ccc",
          marginTop: "15px",
          padding: "10px",
          borderRadius: "6px",
          backgroundColor: "#fff",
        }}
        dangerouslySetInnerHTML={{ __html: selectedTableHTML }}
      />
    </div>
  );
};

export default MessagePanel;
