import React from "react";
import { useStateContext } from "../context/StateContext";

const CodePanel = () => {
  const { codeSnippet } = useStateContext();

  return (
    <div>
      <h3>Backend Code</h3>
      <pre
        style={{
          backgroundColor: "#5a5353ff",
          padding: "15px",
          height: "550px",
          overflowY: "auto",
          borderRadius: "6px",
          fontFamily: "monospace",
          fontSize: "14px",
          whiteSpace: "pre-wrap",
          wordWrap: "break-word",
        }}
      >
        {codeSnippet || "Select an action step to view the backend code here..."}
      </pre>
    </div>
  );
};

export default CodePanel;
