import React from 'react';
import { useStateContext } from '../context/StateContext';
import { useNavigate } from 'react-router-dom';

const MessagePanel = () => {
  const { messages, normalizedTables, setSelectedTable } = useStateContext();
  const navigate = useNavigate();


  const handleTableClick = (tableName) => {
    setSelectedTable(tableName);
    navigate('/table-view');
  };

  // Helper function to remove file extension (like .csv)
  const stripExtension = (filename) => {
    return filename.split('.')[0];
  };

  return (
    <div>
      <h4>Messages</h4>
      {messages.map((msg, index) => (
        <div key={index}>{msg}</div>
      ))}

      {normalizedTables.map((table, index) => (
        <button key={index} onClick={() => handleTableClick(table)}>
          {stripExtension(table)}
        </button>
      ))}
    </div>
  );
};

export default MessagePanel;
