import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  // Break points for curve lines: 
  // { lineKey (e.g. "line-0"): [{ x, y, t }, ...] }
  // 't' = normalized position (0 to 1) along the curve segment (for potential future use)
  const [breakPoints, setBreakPoints] = useState({});

  // Ref to track if initial positions loaded or set
  const positionsInitialized = useRef(false);

  // Box and spacing sizes
  const boxWidth = 140;
  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  // Radius for dots on curve lines (break points)
  const curveDotRadius = 6;

  // Load saved positions from localStorage on mount
  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        const parsedPositions = JSON.parse(savedPositions);
        setPositions(parsedPositions);
        positionsInitialized.current = true;
      } catch {
        console.warn('Failed to parse saved positions');
      }
    }
  }, []);

  // If no saved positions, initialize default positions once
  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};

    let x = 50; // left margin
    let y = 60; // top margin

    workflowSteps.forEach((step) => {
      // Wrap to next line if box won't fit
      if (x + boxWidth > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += boxWidth + horizontalSpacing;
    });

    setPositions((prev) => ({ ...prev, ...initialPositions }));
    positionsInitialized.current = true;
  }, [workflowSteps]);

  // Save positions to localStorage whenever positions change
  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  const handlePositionChange = (step, pos) => {
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  // Helper to calculate cubic Bezier point at t
  // t in [0,1]
  const cubicBezierPoint = (t, p0, p1, p2, p3) => {
    const x = 
      Math.pow(1 - t, 3) * p0.x +
      3 * Math.pow(1 - t, 2) * t * p1.x +
      3 * (1 - t) * Math.pow(t, 2) * p2.x +
      Math.pow(t, 3) * p3.x;
    const y = 
      Math.pow(1 - t, 3) * p0.y +
      3 * Math.pow(1 - t, 2) * t * p1.y +
      3 * (1 - t) * Math.pow(t, 2) * p2.y +
      Math.pow(t, 3) * p3.y;
    return { x, y };
  };

  // Handle drag on curve dot (break point)
  const onCurveDotMouseDown = (lineKey, segmentIndex, t, e) => {
    e.preventDefault();
    e.stopPropagation();
    const svg = e.target.ownerSVGElement;
    if (!svg) return;

    const onMouseMove = (moveEvent) => {
      const pt = svg.createSVGPoint();
      pt.x = moveEvent.clientX;
      pt.y = moveEvent.clientY;
      const cursorpt = pt.matrixTransform(svg.getScreenCTM().inverse());

      setBreakPoints((prev) => {
        const lineBreaks = prev[lineKey] ? [...prev[lineKey]] : [];

        // Clamp point position to a reasonable bounding box if needed
        lineBreaks[segmentIndex] = { x: cursorpt.x, y: cursorpt.y, t };
        return { ...prev, [lineKey]: lineBreaks };
      });
    };

    const onMouseUp = () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);
    };

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);
  };

  // Render a single cubic bezier path with break points as multiple cubic segments
  // Returns SVG <path> elements and draggable <circle> dots on break points
  const renderBezierWithBreakPoints = (lineKey, p0, p1, p2, p3) => {
    const lineBreaks = breakPoints[lineKey] || [];

    // Sort break points by their t value (0 to 1)
    const sortedBreaks = [...lineBreaks].sort((a, b) => a.t - b.t);

    // We will split the bezier into segments: from p0 to first break, between breaks, last break to p3
    // Each segment is itself a cubic bezier curve

    const segments = [];
    let lastT = 0;

    // Helper function to interpolate between two cubic bezier curves
    // Using De Casteljau's algorithm is complicated; for simplicity here,
    // we will just approximate by linear interpolation of control points between t0 and t1
    // This works well enough for user-manipulated bends

    const lerp = (a, b, t) => a + (b - a) * t;
    const lerpPoint = (A, B, t) => ({ x: lerp(A.x, B.x, t), y: lerp(A.y, B.y, t) });

    for (let i = 0; i <= sortedBreaks.length; i++) {
      const startT = lastT;
      const endT = i < sortedBreaks.length ? sortedBreaks[i].t : 1;

      // Calculate control points for segment from startT to endT

      // Use linear interpolation to get points on main bezier at startT and endT
      const segmentP0 = cubicBezierPoint(startT, p0, p1, p2, p3);
      const segmentP3 = cubicBezierPoint(endT, p0, p1, p2, p3);

      // For control points, interpolate between p1 and p2 relative to startT/endT

      // This is an approximation for user-friendly adjustable segments
      const tRange = endT - startT;
      const normalizedP1 = lerpPoint(
        cubicBezierPoint(startT + tRange * 1 / 3, p0, p1, p2, p3),
        segmentP0,
        1
      );
      const normalizedP2 = lerpPoint(
        cubicBezierPoint(startT + tRange * 2 / 3, p0, p1, p2, p3),
        segmentP3,
        1
      );

      segments.push({
        startT,
        endT,
        d: `M${segmentP0.x},${segmentP0.y} C${normalizedP1.x},${normalizedP1.y} ${normalizedP2.x},${normalizedP2.y} ${segmentP3.x},${segmentP3.y}`,
      });

      lastT = endT;
    }

    // Render all segments as separate paths and break points as circles
    return (
      <>
        {segments.map((seg, idx) => (
          <path
            key={`${lineKey}-seg-${idx}`}
            d={seg.d}
            fill="none"
            stroke="rgba(184, 212, 249, 0.9)"
            strokeWidth="1.5"
            markerEnd={idx === segments.length - 1 ? "url(#arrowhead)" : undefined}
          />
        ))}

        {sortedBreaks.map((pt, idx) => (
          <circle
            key={`${lineKey}-dot-${idx}`}
            cx={pt.x}
            cy={pt.y}
            r={curveDotRadius}
            fill="rgba(103, 121, 136, 0.9)"
            stroke="#fff"
            strokeWidth={1}
            style={{ cursor: 'grab' }}
            onMouseDown={(e) => onCurveDotMouseDown(lineKey, idx, pt.t, e)}
          />
        ))}
      </>
    );
  };

  // Render all lines between workflow steps, with breaks and adjusted line endpoints based on box positions
  const renderLines = () => {
    const dotRadius = 4;
    const lines = [];
    const containerWidth = containerRef.current ? containerRef.current.clientWidth : 1000; // fallback width

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const lineKey = `line-${i}`;
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];

      if (from && to) {
        const fromNearRightEdge = from.x + boxWidth > containerWidth - 45;
        const toNearRightEdge = to.x + boxWidth > containerWidth - 45;

        // Determine outgoing start point on 'from' box
        const startX = fromNearRightEdge ? from.x : from.x + boxWidth;
        const startY = from.y + boxHeight / 2;

        // Determine incoming end point on 'to' box
        const endX = toNearRightEdge ? to.x + boxWidth : to.x;
        const endY = to.y + boxHeight / 2;

        // Add/subtract dot radius to have line start/end exactly at dot center
        const adjustedStartX = fromNearRightEdge ? startX : startX;
        const adjustedEndX = toNearRightEdge ? endX : endX;

        // Define control points for the curve
        const deltaX = adjustedEndX - adjustedStartX;
        const deltaY = endY - startY;

        // Calculate control points for smooth cubic Bezier curve
        // Curve bends horizontally or vertically depending on delta
        let controlX1, controlY1, controlX2, controlY2;

        if (Math.abs(deltaX) > Math.abs(deltaY)) {
          controlX1 = adjustedStartX + deltaX / 2;
          controlY1 = startY;
          controlX2 = adjustedStartX + deltaX / 2;
          controlY2 = endY;
        } else {
          controlX1 = adjustedStartX;
          controlY1 = startY + deltaY / 2;
          controlX2 = adjustedEndX;
          controlY2 = startY + deltaY / 2;
        }

        // Render bezier curve with break points
        lines.push(
          <React.Fragment key={lineKey}>
            {renderBezierWithBreakPoints(
              lineKey,
              { x: adjustedStartX, y: startY },
              { x: controlX1, y: controlY1 },
              { x: controlX2, y: controlY2 },
              { x: adjustedEndX, y: endY }
            )}
          </React.Fragment>
        );
      }
    }

    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  return (
    <div
      className="output-panel"
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'auto',
        padding: 20,
      }}
      ref={containerRef}
    >
      {/* Zoom controls */}
      <div
        className="zoom-controls"
        style={{ position: 'absolute', top: 5, right: 5, zIndex: 5 }}
      >
        <button
          className="zoom-btn"
          onClick={zoomIn}
          title="Zoom in"
          style={{ marginRight: 5, textAlign: 'center' }}
        >
          +
        </button>
        <button className="zoom-btn" onClick={zoomOut} title="Zoom out">
          âˆ’
        </button>
      </div>

      {/* Canvas wrapper */}
      <div
        className="canvas-wrapper"
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          position: 'relative',
          minHeight: '100%',
          width: '100%',
        }}
      >
        {/* SVG lines */}
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none',
            overflow: 'visible',
            top: 0,
            left: 0,
          }}
        >
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              fill="rgba(103, 121, 136, 0.9)"
            >
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {/* Draggable steps */}
        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
