import React, { useEffect, useState } from 'react';
import { useStateContext } from '../context/StateContext';
import { fetchTableData } from '../api';
import { useNavigate } from 'react-router-dom';

const NormalizedTableView = () => {
  const { selectedTable, setSelectedTable } = useStateContext();
  const [rows, setRows] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!selectedTable) {
      // No table selected, redirect back to home or main page
      navigate('/');
      return;
    }

    fetchTableData(selectedTable)
      .then((data) => {
        setRows(data);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setRows([]);
      });
  }, [selectedTable, navigate]);

  const handleBack = () => {
    setSelectedTable('');
    navigate('/');
  };

  if (error)
    return (
      <div style={{ padding: 20 }}>
        <h2>Error loading table: {error}</h2>
        <button onClick={handleBack}>Back</button>
      </div>
    );

  if (rows.length === 0)
    return (
      <div style={{ padding: 20 }}>
        <h2>Loading or no data found for table: {selectedTable}</h2>
        <button onClick={handleBack}>Back</button>
      </div>
    );

  return (
    <div style={{ padding: 20 }}>
      <h2>Table: {selectedTable}</h2>
      <button onClick={handleBack} style={{ marginBottom: 10 }}>
        Back
      </button>
      <div style={{ overflowX: 'auto' }}>
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              {Object.keys(rows[0]).map((col) => (
                <th key={col}>{col}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {rows.map((row, idx) => (
              <tr key={idx}>
                {Object.values(row).map((val, i) => (
                  <td key={i}>{val}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default NormalizedTableView;
