import React, { useRef, useState, useEffect } from 'react';
import { useStateContext } from '../context/StateContext';
import Draggable from 'react-draggable';

const OutputPanel = () => {
  const { workflowSteps, fetchCode, isLoading } = useStateContext();
  const [positions, setPositions] = useState({});
  const panelRef = useRef(null);

  const handleDrag = (e, data, step) => {
    setPositions(prev => ({
      ...prev,
      [step]: { x: data.x, y: data.y }
    }));
  };

  const handleBlockClick = (step) => {
    fetchCode(step);
  };

useEffect(() => {
  setPositions(prevPositions => {
    let updated = false;
    const newPositions = { ...prevPositions };
    workflowSteps.forEach((step, index) => {
      if (!newPositions[step]) {
        newPositions[step] = { x: 50, y: 60 * index };
        updated = true;
      }
    });
    // Only update state if there are new positions
    return updated ? newPositions : prevPositions;
  });
}, [workflowSteps]);


  const renderWires = () => {
    const wires = [];
    const panelRect = panelRef.current?.getBoundingClientRect();

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const fromStep = workflowSteps[i];
      const toStep = workflowSteps[i + 1];
      const fromPos = positions[fromStep];
      const toPos = positions[toStep];

      if (fromPos && toPos && panelRect) {
        wires.push(
          <line
            key={`${fromStep}-${toStep}`}
            x1={fromPos.x + 100}
            y1={fromPos.y + 20}
            x2={toPos.x}
            y2={toPos.y + 20}
            stroke="black"
            strokeWidth="2"
          />
        );
      }
    }
    return wires;
  };

  return (
    <div className="output-panel" ref={panelRef}>
      <h2>Workflow Output</h2>
      <div className="workflow-area" style={{ position: 'relative', height: '400px', border: '1px solid #ccc' }}>
        <svg className="wires-svg" style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
          {renderWires()}
        </svg>
        {workflowSteps.map((step) => (
          <Draggable
            key={step}
            position={positions[step]}
            onDrag={(e, data) => handleDrag(e, data, step)}
          >
            <div
              className="workflow-block"
              onClick={() => handleBlockClick(step)}
              style={{
                width: '100px',
                padding: '10px',
                borderRadius: '8px',
                backgroundColor: '#e0e0e0',
                textAlign: 'center',
                position: 'absolute',
                cursor: 'pointer',
                userSelect: 'none',
              }}
            >
              {step}
            </div>
          </Draggable>
        ))}
      </div>
      {isLoading && <p>Loading...</p>}
    </div>
  );
};

export default OutputPanel;
