import React from "react";
import { useStateContext } from "../context/StateContext";

const ActionPanel = () => {
  const { addStep, resetWorkflow } = useStateContext();

  const steps = [
    { label: "Convert to CSV", value: "ConvertCSV" },
    { label: "Clean Modify", value: "cleanModify" },
    { label: "FD Modified", value: "fdModified" },
    { label: "Normalized Table", value: "normalizedTable" },
    { label: "Key Detection", value: "Keys" },
    { label: "Dependency Check", value: "dependencyCheck" },
    { label: "Lossless Check", value: "losslessCheck" },
    { label: "ER Diagram", value: "erDiagram" },
  ];

  return (
    <div>
      <h3>Actions</h3>
      <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        {steps.map((step) => (
          <button
            key={step.value}
            onClick={() => addStep(step.value)}
            style={{
              padding: "10px",
              backgroundColor: "#4d80b3ff",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            {step.label}
          </button>
        ))}
        <button
          onClick={resetWorkflow}
          style={{
            padding: "10px",
            backgroundColor: "#764040ff",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            marginTop: "20px",
          }}
        >
          Reset Workflow
        </button>
      </div>
    </div>
  );
};

export default ActionPanel;
