import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  const positionsInitialized = useRef(false);

  const boxWidth = 140;
  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        const parsedPositions = JSON.parse(savedPositions);
        setPositions(parsedPositions);
        positionsInitialized.current = true;
      } catch {
        console.warn('Failed to parse saved positions');
      }
    }
  }, []);

  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};

    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      if (x + boxWidth > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += boxWidth + horizontalSpacing;
    });

    setPositions((prev) => ({ ...prev, ...initialPositions }));
    positionsInitialized.current = true;
  }, [workflowSteps]);

  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  const handlePositionChange = (step, pos) => {
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  const renderLines = () => {
    const dotRadius = 7;
    const arrowOffset = 4; // To position arrowhead horizontally, slightly offset from box
    const lines = [];

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];

      if (from && to) {
        const startX = from.x + boxWidth + dotRadius;
        const startY = from.y + boxHeight / 2;

        // Position the arrowhead horizontally at the left edge of the target box minus offset
        const endX = to.x - dotRadius - arrowOffset;
        const endY = to.y + boxHeight / 2;

        const deltaX = endX - startX;
        const deltaY = endY - startY;

        let controlX1, controlY1, controlX2, controlY2;
        let pathD;

        // If mostly horizontal distance, curve horizontally (default)
        if (Math.abs(deltaX) > Math.abs(deltaY)) {
          controlX1 = startX + deltaX / 2;
          controlY1 = startY;
          controlX2 = startX + deltaX / 2;
          controlY2 = endY;

          pathD = `M${startX},${startY} C${controlX1},${controlY1} ${controlX2},${controlY2} ${endX},${endY}`;
        } else {
          // Mostly vertical: bend vertically, but keep arrowhead horizontal by adding a little horizontal line at end

          // Midpoint for vertical curve
          controlX1 = startX;
          controlY1 = startY + deltaY / 2;
          controlX2 = endX;
          controlY2 = startY + deltaY / 2;

          // Vertical curve part, ending slightly before horizontal arrowhead start
          const verticalCurveEndX = endX - 8; // a bit before arrowhead startX
          const verticalCurveEndY = endY;

          // Compose path with two parts:
          // 1. vertical cubic Bezier curve from start to verticalCurveEnd
          // 2. horizontal straight line to endX (where arrowhead starts)

          pathD = `
            M${startX},${startY}
            C${controlX1},${controlY1} ${controlX2},${controlY2} ${verticalCurveEndX},${verticalCurveEndY}
            L${endX},${endY}
          `;
        }

        lines.push(
          <path
            key={`line-${i}`}
            d={pathD}
            fill="none"
            stroke="rgba(184, 212, 249, 0.9)"
            strokeWidth="1.5"
            markerEnd="url(#arrowhead)"
          />
        );
      }
    }

    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  return (
    <div
      className="output-panel"
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'auto',
        padding: 20,
      }}
      ref={containerRef}
    >
      {/* Zoom controls */}
      <div
        className="zoom-controls"
        style={{ position: 'absolute', top: 5, right: 5, zIndex: 5 }}
      >
        <button
          className="zoom-btn"
          onClick={zoomIn}
          title="Zoom in"
          style={{ marginRight: 5, textAlign: 'center' }}
        >
          +
        </button>
        <button className="zoom-btn" onClick={zoomOut} title="Zoom out">
          −
        </button>
      </div>

      {/* Canvas wrapper */}
      <div
        className="canvas-wrapper"
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          position: 'relative',
          minHeight: '100%',
          width: '100%',
        }}
      >
        {/* SVG lines */}
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none',
            overflow: 'visible',
            top: 0,
            left: 0,
          }}
        >
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              fill="rgba(103, 121, 136, 0.9)"
            >
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {/* Draggable steps */}
        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
