import React, { createContext, useContext, useState } from 'react';
import axios from 'axios';

const StateContext = createContext();

export const StateProvider = ({ children }) => {
  // Workflow steps for OutputPanel
  const [workflowSteps, setWorkflowSteps] = useState([]);

  // Current code to display in CodePanel
  const [currentCode, setCurrentCodeState] = useState('');

  // Messages for MessagePanel
  const [messages, addMessages] = useState([]);

  // Normalized table names
  const [normalizedTables, setNormalizedTables] = useState([]);

  // Selected table for viewing
  const [selectedTable, setSelectedTable] = useState('');

  // Add step to workflow (OutputPanel)
  const addWorkflowStep = (stepName) => {
    // Prevent duplicates
    if (!workflowSteps.includes(stepName)) {
      setWorkflowSteps((prev) => [...prev, stepName]);
    }
  };

  // Fetch backend code for step
  const fetchCode = async (stepName) => {
    try {
      const response = await axios.get(`/api/code/${stepName}`);
      return response.data.code;
    } catch (error) {
      console.error('Failed to fetch code');
      return 'Error fetching code.';
    }
  };

  // Update current code to show in CodePanel
  const setCurrentCode = (code) => {
    setCurrentCodeState(code);
  };

  // Add message to MessagePanel
  const addMessage = (msg) => {
    setMessages((prev) => [...prev, msg]);
  };

  // Fetch normalized tables from backend
  const fetchNormalizedTables = async () => {
    try {
      const response = await axios.get('/api/normalized_tables');
      setNormalizedTables(response.data.tables || []);
    } catch (error) {
      console.error('Failed to fetch normalized tables');
    }
  };

  return (
    <StateContext.Provider
      value={{
        workflowSteps,
        addWorkflowStep,
        fetchCode,
        currentCode,
        setCurrentCode,
        messages,
        setMessage,
        normalizedTables,
        fetchNormalizedTables,
        selectedTable,
        setSelectedTable
      }}
    >
      {children}
    </StateContext.Provider>
  );
};

export const useStateContext = () => useContext(StateContext);
