import React, { useEffect, useState } from 'react';
import { FaTable } from 'react-icons/fa';

const MessagePanel = () => {
  const [normalizedTables, setNormalizedTables] = useState([]);
  const [selectedTable, setSelectedTable] = useState(null);
  const [selectedTableData, setSelectedTableData] = useState([]);

  useEffect(() => {
    const fetchTables = async () => {
      try {
        const response = await fetch('/api/normalized_tables');
        const data = await response.json();
        console.log("ðŸ“ƒ Normalized Tables:", data.tables);
        setNormalizedTables(data.tables || []);
      } catch (error) {
        console.error("ðŸ’¥ Failed to fetch tables:", error);
      }
    };

    fetchTables();
  }, []);

  const handleTableClick = async (table) => {
    const filename = table.endsWith('.csv') ? table : `${table}.csv`;

    console.log("ðŸ“¤ Clicking table:", table);
    console.log("ðŸ“ Sending request for:", filename);

    try {
      const response = await fetch(`/api/table/${filename}`);
      const data = await response.json();

      if (data.rows && data.rows.length > 0) {
        console.log("âœ… Table data fetched:", data.rows);
        setSelectedTableData(data.rows);
        setSelectedTable(filename);
      } else {
        console.warn("âš ï¸ No rows found or empty table");
        setSelectedTableData([]);
        setSelectedTable(filename);
      }
    } catch (error) {
      console.error("ðŸ’¥ Error fetching table data:", error);
      setSelectedTableData([]);
    }
  };

  const stripExtension = (filename) => filename.replace(/\.csv$/i, '');

  return (
    <div className="message-panel">
      <h3>Normalized Tables</h3>

      {normalizedTables.map((table, index) => (
        <button key={index} onClick={() => handleTableClick(table)}>
          <FaTable style={{ marginRight: '6px' }} />
          {stripExtension(table)}
        </button>
      ))}

      {selectedTable && (
        <div className="table-display">
          <h4>{stripExtension(selectedTable)}</h4>
          {selectedTableData.length > 0 ? (
            <table border="1">
              <thead>
                <tr>
                  {Object.keys(selectedTableData[0]).map((col, idx) => (
                    <th key={idx}>{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {selectedTableData.map((row, idx) => (
                  <tr key={idx}>
                    {Object.values(row).map((val, i) => (
                      <td key={i}>{val}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No data found</p>
          )}
        </div>
      )}
    </div>
  );
};

export default MessagePanel;
