import React from 'react';
import { useStateContext } from '../context/StateContext';
import { useNavigate } from 'react-router-dom';
import { FaTable, FaProjectDiagram } from 'react-icons/fa';

const MessagePanel = () => {
  const { messages, normalizedTables, setSelectedTable, erDiagramGenerated } = useStateContext();
  const navigate = useNavigate();

  const handleTableClick = (tableName) => {
    setSelectedTable(tableName);
    navigate('/table-view');
  };

  const handleERDiagramClick = () => {
    navigate('/er-diagram');
  };

  const stripExtension = (filename) => filename.split('.')[0];

  return (
    <div className="message-panel fancy-scroll">
      {/* Messages Section */}
      <div className="messages-section">
        <h4 className="section-title">Messages</h4>
        <div className="messages-list">
          {messages.map((msg, index) => (
            <div key={index} className="message-item">
              {typeof msg === 'string' ? msg : JSON.stringify(msg)}
            </div>
          ))}
        </div>
      </div>

      {/* Normalized Tables Section */}
      <div className="tables-section">
        <div className="tables-list">
          {normalizedTables.map((table, index) => (
            <button
              key={index}
              className="table-button"
              onClick={() => handleTableClick(table)}
            >
              <FaTable className="table-icon" />
              {stripExtension(table)}
            </button>
          ))}
        </div>
      </div>

      {/* ER Diagram Section */}
      {erDiagramGenerated && (
        <div className="diagram-section">
          <button
            className="diagram-button"
            onClick={handleERDiagramClick}
          >
            <FaProjectDiagram className="diagram-icon" />
            Show ER Diagram
          </button>
        </div>
      )}
    </div>
  );
};

export default MessagePanel;
