import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [boxWidths, setBoxWidths] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  const [controlPoints, setControlPoints] = useState({});
  const positionsLoadedRef = useRef(false); // true when we loaded saved positions
  const [layoutReady, setLayoutReady] = useState(false); // true when we have positions + widths

  // Undo / Redo stacks
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  // friendly names
  const stepDisplayNames = {
    Upload: 'Upload',
    CleanModify: 'Noise Clean',
    FDModified: 'FD Detection',
    NormalizeTable: 'Normalize to 3NF',
    LosslessCheck: 'Lossless Decomposition',
    ERDiagram: 'ER Diagram',
    ConvertToCSV: 'CSV Converter',
    KeyDetection: 'Key Detector',
    DependencyPreservation: 'Check Dependency Preservation',
  };

  // Load saved positions on mount
  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        setPositions(parsed);
        positionsLoadedRef.current = true;
      } catch (e) {
        console.warn('Failed to parse saved positions', e);
      }
    }
  }, []);

  // When we have widths (measured from DraggableStep) and no saved positions,
  // compute initial positions.
  useEffect(() => {
    if (positionsLoadedRef.current) return; // don't overwrite saved positions
    if (!containerRef.current) return;
    if (workflowSteps.length === 0) return;

    // wait until all widths are measured
    if (workflowSteps.some((s) => !boxWidths[s])) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};
    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      const width = boxWidths[step] || 140;
      if (x + width > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += width + horizontalSpacing;
    });

    setPositions((prev) => ({ ...prev, ...initialPositions }));
    positionsLoadedRef.current = true;
  }, [workflowSteps, boxWidths]);

  // Persist positions when they change (only after we've initialized)
  useEffect(() => {
    if (positionsLoadedRef.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  // layoutReady: true when we have position + width for each step
  useEffect(() => {
    if (workflowSteps.length === 0) {
      setLayoutReady(false);
      return;
    }
    const havePos = workflowSteps.every((s) => {
      const p = positions[s];
      return p && typeof p.x === 'number' && typeof p.y === 'number';
    });
    const haveWidths = workflowSteps.every((s) => !!boxWidths[s]);
    setLayoutReady(havePos && haveWidths);
  }, [positions, boxWidths, workflowSteps]);

  // Handle drag position updates (push previous pos to undo stack)
  const handlePositionChange = (step, pos) => {
    setPositions((prev) => {
      setUndoStack((u) => [...u, prev]);
      setRedoStack([]); // clear redo when new action
      return { ...prev, [step]: pos };
    });
  };

  const handleClickStep = async (step) => {
    try {
      const res = await axios.get(`/api/code/${step}`);
      setCurrentCode(res.data.code || '');
    } catch (e) {
      setCurrentCode('Failed to load code');
    }
  };

  // Undo (pop 2 states)
  const handleUndo = () => {
    if (undoStack.length === 0) return;
    setPositions((currentPositions) => {
      let newUndo = [...undoStack];
      let newRedo = [...redoStack, currentPositions];
      let previous;
      for (let i = 0; i < 2; i++) {
        if (newUndo.length === 0) break;
        previous = newUndo.pop();
        newRedo.push(previous);
      }
      setUndoStack(newUndo);
      setRedoStack(newRedo);
      return previous || currentPositions;
    });
  };

  // Redo (pop 2 states)
  const handleRedo = () => {
    if (redoStack.length === 0) return;
    setPositions((currentPositions) => {
      let newRedo = [...redoStack];
      let newUndo = [...undoStack, currentPositions];
      let next;
      for (let i = 0; i < 2; i++) {
        if (newRedo.length === 0) break;
        next = newRedo.pop();
        newUndo.push(next);
      }
      setRedoStack(newRedo);
      setUndoStack(newUndo);
      return next || currentPositions;
    });
  };

  // cubic bezier helper
  function cubicBezierPoint(t, P0, P1, P2, P3) {
    const u = 1 - t;
    const tt = t * t;
    const uu = u * u;
    const uuu = uu * u;
    const ttt = tt * t;
    const x =
      uuu * P0.x +
      3 * uu * t * P1.x +
      3 * u * tt * P2.x +
      ttt * P3.x;
    const y =
      uuu * P0.y +
      3 * uu * t * P1.y +
      3 * u * tt * P2.y +
      ttt * P3.y;
    return { x, y };
  }

  // render lines only when layoutReady
  const renderLines = () => {
    const dotRadius = 4.5;
    const lines = [];
    const t1 = 0.25;
    const t2 = 0.75;
    const padding = 6;

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const fromStep = workflowSteps[i];
      const toStep = workflowSteps[i + 1];
      const from = positions[fromStep];
      const to = positions[toStep];

      if (!from || !to) continue; // safety
      const fromWidth = boxWidths[fromStep] || 140;
      // const toWidth = boxWidths[toStep] || 140; // not needed here

      // start at right edge center of from
      const P0 = { x: from.x + fromWidth + padding, y: from.y + boxHeight / 2 };
      // end at left edge center of to
      const P3 = { x: to.x - padding, y: to.y + boxHeight / 2 };

      const cp = controlPoints[i] || {};
      let P1, P2;
      if (cp.c1 && cp.c2) {
        P1 = cp.c1;
        P2 = cp.c2;
      } else {
        const dx = P3.x - P0.x;
        const dy = P3.y - P0.y;
        if (Math.abs(dx) > Math.abs(dy)) {
          P1 = { x: P0.x + dx / 2, y: P0.y };
          P2 = { x: P0.x + dx / 2, y: P3.y };
        } else {
          P1 = { x: P0.x, y: P0.y + dy / 2 };
          P2 = { x: P3.x, y: P0.y + dy / 2 };
        }
      }

      const dot1Pos = cubicBezierPoint(t1, P0, P1, P2, P3);
      const dot2Pos = cubicBezierPoint(t2, P0, P1, P2, P3);
      const d = `M${P0.x},${P0.y} C${P1.x},${P1.y} ${P2.x},${P2.y} ${P3.x},${P3.y}`;

      lines.push(
        <path
          key={`line-${i}`}
          d={d}
          fill="none"
          stroke="rgba(184, 212, 249, 0.9)"
          strokeWidth="1.5"
          markerEnd="url(#arrowhead)"
        />
      );

      lines.push(
        <circle
          key={`dot1-${i}`}
          cx={dot1Pos.x}
          cy={dot1Pos.y}
          r={dotRadius}
          pointerEvents="all"
          style={{ cursor: 'grab' }}
          onMouseDown={(e) => onMouseDownControlPoint(i, 'c1', e)}
        />,
        <circle
          key={`dot2-${i}`}
          cx={dot2Pos.x}
          cy={dot2Pos.y}
          r={dotRadius}
          pointerEvents="all"
          style={{ cursor: 'grab' }}
          onMouseDown={(e) => onMouseDownControlPoint(i, 'c2', e)}
        />
      );
    }

    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  // control points dragging
  const draggingRef = useRef(null);

  const onMouseDownControlPoint = (lineIndex, pointKey, e) => {
    e.preventDefault();
    e.stopPropagation();
    draggingRef.current = { lineIndex, pointKey };
    window.addEventListener('mousemove', onMouseMoveControlPoint);
    window.addEventListener('mouseup', onMouseUpControlPoint);
  };

  const onMouseMoveControlPoint = (e) => {
    if (!draggingRef.current) return;
    const { lineIndex, pointKey } = draggingRef.current;
    const containerRect = containerRef.current.getBoundingClientRect();
    const mouseX = (e.clientX - containerRect.left) / scale;
    const mouseY = (e.clientY - containerRect.top) / scale;
    setControlPoints((prev) => ({
      ...prev,
      [lineIndex]: {
        ...prev[lineIndex],
        [pointKey]: { x: mouseX, y: mouseY },
      },
    }));
  };

  const onMouseUpControlPoint = () => {
    draggingRef.current = null;
    window.removeEventListener('mousemove', onMouseMoveControlPoint);
    window.removeEventListener('mouseup', onMouseUpControlPoint);
  };

  // get widths from child
  const handleSizeChange = (step, width) => {
    setBoxWidths((prev) => {
      if (prev[step] === width) return prev;
      return { ...prev, [step]: width };
    });
  };

  return (
    <div
      className="output-panel"
      style={{ width: '100%', height: '100%', position: 'relative', overflow: 'auto', padding: 20 }}
      ref={containerRef}
    >
      <div
        className="zoom-controls"
        style={{ position: 'absolute', top: 5, right: 5, zIndex: 5, display: 'flex', gap: 6, alignItems: 'center' }}
      >
        <button className="zoom-btn" onClick={zoomIn} title="Zoom in">+</button>
        <button className="zoom-btn" onClick={zoomOut} title="Zoom out">−</button>
        <button className="undo-btn" onClick={handleUndo} disabled={undoStack.length === 0} title="Undo (2 steps)">↺</button>
        <button className="redo-btn" onClick={handleRedo} disabled={redoStack.length === 0} title="Redo (2 steps)">↻</button>
      </div>

      <div className="canvas-wrapper" style={{ transform: `scale(${scale})`, transformOrigin: '0 0', position: 'relative', minHeight: '100%', width: '100%' }}>
        <svg style={{ position: 'absolute', width: '100%', height: '100%', pointerEvents: 'none', overflow: 'visible', top: 0, left: 0 }}>
          <defs>
            <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto" fill="rgba(103,121,136,0.9)">
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {layoutReady && renderLines()}
        </svg>

        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            displayName={stepDisplayNames[step] || step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
            onSizeChange={handleSizeChange}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
