import React, { useRef } from 'react';
import axios from 'axios';
import { useStateContext } from '../context/StateContext';

const ActionPanel = () => {
  const { addWorkflowStep, addMessage } = useStateContext();
  const fileInputRef = useRef(null);

  // Handle File Upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      addMessage(response.data.message || 'File uploaded successfully');
      addWorkflowStep('Upload');
    } catch (error) {
      addMessage(error.response?.data?.message || 'File upload failed');
    }
  };

  // Action Handlers
  const handleConvertToCSV = async () => {
    try {
      const response = await axios.post('/api/convert_to_csv');
      addMessage(response.data.message || 'Converted to CSV');
      addWorkflowStep('ConvertToCSV');
    } catch (error) {
      addMessage(error.response?.data?.message || 'Conversion failed');
    }
  };

  const handleCleanModify = async () => {
    try {
      const response = await axios.post('/api/clean_modify');
      addMessage(response.data.message || 'Data cleaned');
      addWorkflowStep('CleanModify');
    } catch (error) {
      addMessage(error.response?.data?.message || 'Clean modify failed');
    }
  };

  const handleFDModified = async () => {
    try {
      const response = await axios.post('/api/fd_modified');
      addMessage(response.data.message || 'Functional dependencies detected');
      addWorkflowStep('FDModified');
    } catch (error) {
      addMessage(error.response?.data?.message || 'FD detection failed');
    }
  };

  const handleNormalizeTable = async () => {
    try {
      const response = await axios.post('/api/normalize_table');
      addMessage(response.data.message || 'Tables normalized');
      addWorkflowStep('NormalizeTable');
    } catch (error) {
      addMessage(error.response?.data?.message || 'Normalization failed');
    }
  };

  const handleKeyDetection = async () => {
    try {
      const response = await axios.post('/api/key_detection');
      addMessage(response.data.message || 'Keys detected');
      addWorkflowStep('KeyDetection');
    } catch (error) {
      addMessage(error.response?.data?.message || 'Key detection failed');
    }
  };

  const handleDependencyPreservation = async () => {
    try {
      const response = await axios.post('/api/dependency_preservation');
      setMessage(response.data.message || 'Dependency preservation checked');
      addWorkflowStep('DependencyPreservation');
    } catch (error) {
      setMessage(error.response?.data?.message || 'Dependency preservation check failed');
    }
  };

  const handleLosslessCheck = async () => {
    try {
      const response = await axios.post('/api/lossless_check');
      setMessage(response.data.message || 'Lossless decomposition checked');
      addWorkflowStep('LosslessCheck');
    } catch (error) {
      addMessage(error.response?.data?.message || 'Lossless check failed');
    }
  };

  const handleGenerateERDiagram = async () => {
    try {
      const response = await axios.post('/api/generate_er_diagram');
      addMessage(response.data.message || 'ER Diagram generated');
      addWorkflowStep('ERDiagram');
    } catch (error) {
      setMessage(error.response?.data?.message || 'ER Diagram generation failed');
    }
  };

  return (
    <div className="action-panel">
      <h2>Action Panel</h2>

      {/* File Upload Section */}
      <div>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileUpload}
          style={{ display: 'none' }}
        />
        <button onClick={() => fileInputRef.current.click()}>Upload File</button>
      </div>

      {/* Action Buttons */}
      <div className="actions">
        <button onClick={handleConvertToCSV}>Convert to CSV</button>
        <button onClick={handleCleanModify}>Clean & Modify</button>
        <button onClick={handleFDModified}>Detect Functional Dependencies</button>
        <button onClick={handleNormalizeTable}>Normalize Tables</button>
        <button onClick={handleKeyDetection}>Detect Keys</button>
        <button onClick={handleDependencyPreservation}>Check Dependency Preservation</button>
        <button onClick={handleLosslessCheck}>Check Lossless Decomposition</button>
        <button onClick={handleGenerateERDiagram}>Generate ER Diagram</button>
      </div>
    </div>
  );
};

export default ActionPanel;
