// src/components/MessagePanel.jsx
import React from "react";
import { useStateContext } from "../context/StateContext";
import { useNavigate } from "react-router-dom";

const MessagePanel = () => {
  const { messages, normalizedTables, setMessages } = useStateContext();
  const navigate = useNavigate();

  const handleTableClick = (tableName) => {
    navigate(`/table/${encodeURIComponent(tableName)}`);
    setMessages((prev) => [...prev, `ðŸ“Š Showing table: ${tableName}`]);
  };

  return (
    <div
      style={{
        padding: "10px",
        backgroundColor: "#1b1b1b",
        color: "#fff",
        height: "100%",
        overflowY: "auto",
        borderRadius: "10px",
      }}
    >
      <h3>Messages & Tables</h3>
      <div style={{ marginBottom: "10px" }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{ marginBottom: "5px" }}>
            {msg}
          </div>
        ))}
      </div>

      {normalizedTables.length > 0 && (
        <div>
          <h4>Normalized Tables (3NF)</h4>
          <div style={{ display: "flex", flexWrap: "wrap", gap: "8px" }}>
            {normalizedTables.map((table) => (
              <button
                key={table}
                onClick={() => handleTableClick(table)}
                style={{
                  padding: "6px 12px",
                  borderRadius: "6px",
                  border: "none",
                  backgroundColor: "#4d80b3ff",
                  color: "white",
                  cursor: "pointer",
                  whiteSpace: "nowrap",
                }}
              >
                {table}
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default MessagePanel;
