import React, { useRef } from 'react';
import { useStateContext } from '../context/StateContext';

const ActionPanel = () => {
  const { addStep, uploadedFile, isLoading } = useStateContext();
  const fileInputRef = useRef(null);

  const handleAddStep = (stepName) => {
    if (stepName === 'UploadFile') {
      fileInputRef.current.click();
    } else {
      addStep(stepName);
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      addStep('UploadFile', file);
    }
  };

  return (
    <div className="action-panel">
      <h2>Action Panel</h2>

      <button onClick={() => handleAddStep('UploadFile')} disabled={isLoading}>
        Upload File
      </button>
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />

      <button onClick={() => handleAddStep('CleanModify')} disabled={isLoading || !uploadedFile}>
        Clean Modify
      </button>

      <button onClick={() => handleAddStep('FDModified')} disabled={isLoading || !uploadedFile}>
        FD Modified
      </button>

      <button onClick={() => handleAddStep('NormalizeTables')} disabled={isLoading || !uploadedFile}>
        Normalize Tables
      </button>

      <button onClick={() => handleAddStep('DependencyPreservation')} disabled={isLoading || !uploadedFile}>
        Dependency Preservation
      </button>

      <button onClick={() => handleAddStep('LosslessCheck')} disabled={isLoading || !uploadedFile}>
        Lossless Check
      </button>

      <button onClick={() => handleAddStep('GenerateERDiagram')} disabled={isLoading || !uploadedFile}>
        Generate ER Diagram
      </button>
    </div>
  );
};

export default ActionPanel;
