import React, { useEffect, useState } from "react";
import mermaid from "mermaid";
import panzoom from "panzoom";

const ERDiagramPage = () => {
  const [mermaidCode, setMermaidCode] = useState("");
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/er_diagram")
      .then((res) => res.json())
      .then((data) => {
        if (data.mermaid_code) {
          setMermaidCode(data.mermaid_code);
        } else {
          setError("No ER diagram data received");
        }
      })
      .catch((e) => setError(e.message));
  }, []);

  useEffect(() => {
    if (mermaidCode) {
      mermaid.initialize({ startOnLoad: false });
      const renderId = "er-diagram";
      mermaid.render(renderId, mermaidCode, (svgCode) => {
        const container = document.getElementById("er-container");
        container.innerHTML = svgCode;

        // Enable drag + zoom
        const svg = container.querySelector("svg");
        if (svg) {
          panzoom(svg, { maxZoom: 5, minZoom: 0.5 });
        }
      });
    }
  }, [mermaidCode]);

  if (error) return <div>Error: {error}</div>;
  if (!mermaidCode) return <div>Loading ER Diagram...</div>;

  return (
    <div>
      <h2>Entity-Relationship Diagram</h2>
      <div
        id="er-container"
        style={{
          border: "1px solid #ccc",
          height: "80vh",
          overflow: "hidden",
          background: "#fafafa",
        }}
      ></div>
    </div>
  );
};

export default ERDiagramPage;
