import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';

const ERDiagramPage = () => {
  const [imageURL, setImageURL] = useState(null);
  const [error, setError] = useState(null);
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);
  const imgRef = useRef(null);
  const pos = useRef({ x: 0, y: 0, isDragging: false, startX: 0, startY: 0 });

  useEffect(() => {
    const fetchERDiagram = async () => {
      try {
        const response = await axios.get('/api/get_er_diagram_image', { responseType: 'blob' });
        const blobUrl = URL.createObjectURL(response.data);
        setImageURL(blobUrl);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load ER Diagram');
      }
    };

    fetchERDiagram();
  }, []);

  // Mouse wheel zoom
  const handleWheel = (e) => {
    e.preventDefault();
    const zoomSpeed = 0.1;
    if (e.deltaY < 0) {
      setScale((prev) => Math.min(prev + zoomSpeed, 5));
    } else {
      setScale((prev) => Math.max(prev - zoomSpeed, 0.5));
    }
  };

  // Drag start
  const handleMouseDown = (e) => {
    pos.current.isDragging = true;
    pos.current.startX = e.clientX - pos.current.x;
    pos.current.startY = e.clientY - pos.current.y;
  };

  // Drag move
  const handleMouseMove = (e) => {
    if (!pos.current.isDragging) return;
    pos.current.x = e.clientX - pos.current.startX;
    pos.current.y = e.clientY - pos.current.startY;
    imgRef.current.style.transform = `translate(${pos.current.x}px, ${pos.current.y}px) scale(${scale})`;
  };

  // Drag end
  const handleMouseUp = () => {
    pos.current.isDragging = false;
  };

  // Reset zoom and position
  const resetView = () => {
    setScale(1);
    pos.current = { x: 0, y: 0, isDragging: false, startX: 0, startY: 0 };
    imgRef.current.style.transform = `translate(0px, 0px) scale(1)`;
  };

  return (
    <div className="er-page">
      <h2 className="er-title">ER Diagram</h2>
      {error && <div className="er-error">{error}</div>}
      {imageURL && (
        <>
          <div className="zoom-controls">
            <button onClick={resetView}>Reset</button>
          </div>
          <div
            ref={containerRef}
            className="er-container"
            onWheel={handleWheel}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
          >
            <img
              ref={imgRef}
              src={imageURL}
              alt="ER Diagram"
              className="er-image"
              style={{
                transform: `translate(0px, 0px) scale(${scale})`,
              }}
            />
          </div>
        </>
      )}
    </div>
  );
};

export default ERDiagramPage;
