import React, { createContext, useContext, useState, useEffect, useMemo, useCallback } from "react";
import {
  fetchNormalizedTables,
  fetchCodeForStep,
  fetchTableHTML,
  validateStep,
  uploadFile,
} from "../api/api";

const StateContext = createContext();

export const StateProvider = ({ children }) => {
  // State initialization with localStorage persistence
  const [workflowSteps, setWorkflowSteps] = useState(() => {
    const saved = localStorage.getItem('workflowSteps');
    return saved ? JSON.parse(saved) : [];
  });

  const [codeSnippet, setCodeSnippet] = useState("");
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem('messages');
    return saved ? JSON.parse(saved) : [];
  });
  
  const [normalizedTables, setNormalizedTables] = useState([]);
  const [selectedTableHTML, setSelectedTableHTML] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [pagination, setPagination] = useState({
    currentPage: 1,
    itemsPerPage: 10,
    totalItems: 0
  });

  // Save to localStorage when state changes
  useEffect(() => {
    localStorage.setItem('workflowSteps', JSON.stringify(workflowSteps));
  }, [workflowSteps]);

  useEffect(() => {
    localStorage.setItem('messages', JSON.stringify(messages));
  }, [messages]);

  const addStep = useCallback(async (stepName, file = null) => {
    setIsLoading(true);
    try {
      const res = await validateStep(workflowSteps, stepName);
      if (res.valid) {
        const newSteps = [...workflowSteps, stepName];
        setWorkflowSteps(newSteps);
        setMessages(prev => [...prev, `✅ Step "${stepName}" added to workflow.`]);
        
        if (stepName === "UploadFile" && file) {
          try {
            await uploadFile(file);
            setMessages(prev => [...prev, "📁 File uploaded successfully."]);
          } catch (uploadError) {
            setMessages(prev => [...prev, `❌ Upload failed: ${uploadError.message}`]);
            setWorkflowSteps(prev => prev.filter(step => step !== stepName));
          }
        }
      } else {
        setMessages(prev => [...prev, `❌ ${res.message}`]);
      }
    } catch (error) {
      setMessages(prev => [...prev, `⚠️ Error adding step: ${error.message}`]);
    } finally {
      setIsLoading(false);
    }
  }, [workflowSteps]);

  const selectTable = useCallback(async (tableName) => {
    setIsLoading(true);
    try {
      const html = await fetchTableHTML(tableName);
      setSelectedTableHTML(html);
      setMessages(prev => [...prev, `📊 Loaded table: ${tableName}`]);
    } catch (error) {
      setMessages(prev => [...prev, `❌ Error loading table: ${error.message}`]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  const loadNormalizedTables = useCallback(async () => {
    setIsLoading(true);
    try {
      const tables = await fetchNormalizedTables();
      setNormalizedTables(tables);
      setPagination(prev => ({
        ...prev,
        totalItems: tables.length
      }));
      setMessages(prev => [...prev, `✅ Loaded ${tables.length} normalized tables`]);
    } catch (error) {
      setMessages(prev => [...prev, `❌ Error loading tables: ${error.message}`]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  const fetchCode = useCallback(async (stepName) => {
    setIsLoading(true);
    try {
      const code = await fetchCodeForStep(stepName);
      setCodeSnippet(code);
      setMessages(prev => [...prev, `📝 Loaded code for: ${stepName}`]);
    } catch (error) {
      setMessages(prev => [...prev, `❌ Error loading code: ${error.message}`]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  const resetWorkflow = useCallback(() => {
    setWorkflowSteps([]);
    setCodeSnippet("");
    setMessages(["🔄 Workflow reset."]);
    setSelectedTableHTML("");
    setNormalizedTables([]);
    setPagination({
      currentPage: 1,
      itemsPerPage: 10,
      totalItems: 0
    });
    localStorage.removeItem('workflowSteps');
    localStorage.removeItem('messages');
  }, []);

  const clearMessages = useCallback(() => {
    setMessages([]);
    localStorage.removeItem('messages');
  }, []);

  const updateMessage = useCallback((index, newMessage) => {
    setMessages(prev => prev.map((msg, i) => i === index ? newMessage : msg));
  }, []);

  const goToPage = useCallback((pageNumber) => {
    setPagination(prev => ({
      ...prev,
      currentPage: Math.max(1, Math.min(pageNumber, Math.ceil(prev.totalItems / prev.itemsPerPage)))
    }));
  }, []);

  // Memoize context value to prevent unnecessary re-renders
  const value = useMemo(() => ({
    workflowSteps,
    codeSnippet,
    messages,
    normalizedTables,
    selectedTableHTML,
    isLoading,
    pagination,
    addStep,
    fetchCode,
    loadNormalizedTables,
    selectTable,
    resetWorkflow,
    clearMessages,
    updateMessage,
    goToPage,
    setPagination
  }), [
    workflowSteps,
    codeSnippet,
    messages,
    normalizedTables,
    selectedTableHTML,
    isLoading,
    pagination,
    addStep,
    fetchCode,
    loadNormalizedTables,
    selectTable,
    resetWorkflow,
    clearMessages,
    updateMessage,
    goToPage
  ]);

  return (
    <StateContext.Provider value={value}>
      {children}
    </StateContext.Provider>
  );
};

export const useStateContext = () => {
  const context = useContext(StateContext);
  if (context === undefined) {
    throw new Error('useStateContext must be used within a StateProvider');
  }
  return context;
};
