import React from 'react';
import classNames from 'classnames';

const DraggableStep = ({ step, position, onPositionChange, onClick, isSelected }) => {
  const handleDrag = (e) => {
    const newX = e.clientX - 80;
    const newY = e.clientY - 20;
    onPositionChange(step, { x: newX, y: newY });
  };

  return (
    <div
      className={classNames(
        'absolute cursor-pointer rounded-lg p-2 text-sm shadow-md transition',
        isSelected ? 'bg-blue-500 text-white' : 'bg-white text-gray-800 hover:bg-blue-100'
      )}
      onMouseDown={(e) => e.preventDefault()}
      onClick={() => onClick(step)}
      onDrag={(e) => handleDrag(e)}
      draggable
      style={{ left: position.x, top: position.y, width: 150, textAlign: 'center' }}
    >
      {step}
    </div>
  );
};

export default DraggableStep;
