import React, { createContext, useContext, useState, useCallback, useMemo } from 'react';
import { 
  fetchNormalizedTables, 
  fetchCodeForStep, 
  fetchTableHTML, 
  validateStep, 
  uploadFile 
} from '../api/api';

const StateContext = createContext();

export const StateProvider = ({ children }) => {
  const [workflowSteps, setWorkflowSteps] = useState([]);
  const [codeSnippet, setCodeSnippet] = useState('');
  const [messages, setMessages] = useState([]);
  const [normalizedTables, setNormalizedTables] = useState([]);
  const [selectedTableHTML, setSelectedTableHTML] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);

  // Add workflow step & handle Upload separately
  const addStep = useCallback(async (stepName, file = null) => {
    setIsLoading(true);
    try {
      // Validate workflow sequence
      const res = await validateStep(workflowSteps, stepName);
      if (res.valid) {
        setWorkflowSteps(prev => [...prev, stepName]);
        setMessages(prev => [...prev, `✅ "${stepName}" triggered`]);

        // Handle UploadFile step with file upload
        if (stepName === 'UploadFile' && file) {
          await uploadFile(file);
          setUploadedFile(file.name);
          setMessages(prev => [...prev, '📁 File uploaded successfully']);
        }

        // After Normalization step, auto-load normalized tables
        if (stepName === 'NormalizeTables') {
          await loadNormalizedTables();
        }
      } else {
        setMessages(prev => [...prev, `❌ ${res.message}`]);
      }
    } catch (error) {
      setMessages(prev => [...prev, `⚠️ Error: ${error.message}`]);
    } finally {
      setIsLoading(false);
    }
  }, [workflowSteps, loadNormalizedTables]);



  // Fetch code snippet when OutputPanel block is clicked
  const fetchCode = useCallback(async (stepName) => {
    setIsLoading(true);
    try {
      const code = await fetchCodeForStep(stepName);
      setCodeSnippet(code);
    } catch (error) {
      setMessages(prev => [...prev, `❌ Failed to fetch code`]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Load Table HTML content (Excel-like Viewer)
  const selectTable = useCallback(async (tableName) => {
    setIsLoading(true);
    try {
      const html = await fetchTableHTML(tableName);
      setSelectedTableHTML(html);
    } catch (error) {
      setMessages(prev => [...prev, `❌ Failed to load table`]);
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Reset Workflow to initial state
  const resetWorkflow = useCallback(() => {
    setWorkflowSteps([]);
    setCodeSnippet('');
    setMessages(['🔄 Workflow reset']);
    setNormalizedTables([]);
    setSelectedTableHTML('');
    setUploadedFile(null);
  }, []);

  const value = useMemo(() => ({
    workflowSteps,
    codeSnippet,
    messages,
    normalizedTables,
    selectedTableHTML,
    isLoading,
    uploadedFile,
    addStep,
    fetchCode,
    loadNormalizedTables,
    selectTable,
    resetWorkflow
  }), [
    workflowSteps,
    codeSnippet,
    messages,
    normalizedTables,
    selectedTableHTML,
    isLoading,
    uploadedFile,
    addStep,
    fetchCode,
    loadNormalizedTables,
    selectTable,
    resetWorkflow
  ]);

  return (
    <StateContext.Provider value={value}>
      {children}
    </StateContext.Provider>
  );
};

export const useStateContext = () => {
  const context = useContext(StateContext);
  if (!context) {
    throw new Error('useStateContext must be used within a StateContextProvider');
  }
  return context;
};
