import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ERDiagramPage = () => {
  const [imageURL, setImageURL] = useState(null);
  const [error, setError] = useState(null);
  const [scale, setScale] = useState(1);

  useEffect(() => {
    const fetchERDiagram = async () => {
      try {
        const response = await axios.get('/api/get_er_diagram_image', { responseType: 'blob' });
        const blobUrl = URL.createObjectURL(response.data);
        setImageURL(blobUrl);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load ER Diagram');
      }
    };

    fetchERDiagram();
  }, []);

  const zoomIn = () => setScale(prev => Math.min(prev + 0.1, 5));
  const zoomOut = () => setScale(prev => Math.max(prev - 0.1, 0.5));
  const resetZoom = () => setScale(1);

  return (
    <div className="er-page">
      <h2 className="er-title">ER Diagram</h2>
      {error && <div className="er-error">{error}</div>}
      {imageURL && (
        <>
          <div className="zoom-controls">
            <button onClick={zoomIn}>+</button>
            <button onClick={zoomOut}>-</button>
            <button onClick={resetZoom}>Reset</button>
          </div>
          <div className="er-container">
            <img
              src={imageURL}
              alt="ER Diagram"
              className="er-image"
              style={{ transform: `scale(${scale})` }}
            />
          </div>
        </>
      )}
    </div>
  );
};

export default ERDiagramPage;
