import React from 'react';
import { useDrag } from '@use-gesture/react';

const DraggableStep = ({ step, displayName, position, onPositionChange, onClick, isFirst }) => {
  const bind = useDrag(({ offset: [x, y] }) => {
    onPositionChange(step, { x, y });
  });

  return (
    <div
      {...bind()}
      onClick={() => onClick(step)}
      className="draggable-node"
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: 140,
        height: 44,
        backgroundColor: '#a5c9ff',
        borderRadius: 6,
        boxShadow: '0 2px 6px rgba(0,0,0,0.2)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: 'grab',
        userSelect: 'none',
        fontWeight: 'bold',
        color: '#003366',
        padding: '0 10px',
        boxSizing: 'border-box',
      }}
      title={displayName}
    >
      {/* Left connection dot, shown only if not first */}
      {!isFirst && (
        <div
          className="connection-dot left-dot"
          style={{
            width: 12,
            height: 12,
            borderRadius: '50%',
            backgroundColor: '#4484f6',
            position: 'absolute',
            left: -6,
            top: '50%',
            transform: 'translateY(-50%)',
          }}
        />
      )}

      {/* Label */}
      <div className="step-label" style={{ pointerEvents: 'none' }}>
        {displayName || step}
      </div>

      {/* Right connection dot always visible */}
      <div
        className="connection-dot right-dot"
        style={{
          width: 12,
          height: 12,
          borderRadius: '50%',
          backgroundColor: '#4484f6',
          position: 'absolute',
          right: -6,
          top: '50%',
          transform: 'translateY(-50%)',
        }}
      />
    </div>
  );
};

export default DraggableStep;
