import React, { useRef, useEffect, useState } from 'react';
import { useStateContext } from '../context/StateContext';
import Draggable from 'react-draggable';
import axios from 'axios';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const panelRef = useRef(null);
  const [positions, setPositions] = useState({});

  useEffect(() => {
    const initialPositions = {};
    workflowSteps.forEach((step, index) => {
      initialPositions[step] = { x: 50, y: 50 + index * 80 };
    });
    setPositions((prev) => ({ ...prev, ...initialPositions }));
  }, [workflowSteps]);

  const handleDrag = (e, data, step) => {
    setPositions((prev) => ({
      ...prev,
      [step]: { x: data.x, y: data.y },
    }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch (error) {
      setCurrentCode('Failed to load code');
    }
  };

  const renderLines = () => {
    const lines = [];
    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];
      if (from && to) {
        lines.push(
          <line
            key={`line-${i}`}
            x1={from.x + 60}
            y1={from.y + 20}
            x2={to.x + 60}
            y2={to.y + 20}
            stroke="black"
            strokeWidth="2"
          />
        );
      }
    }
    return lines;
  };

  return (
    <div className="output-panel" ref={panelRef} style={{ width: '100%', height: '100%', position: 'relative' }}>
      <svg style={{ position: 'absolute', width: '100%', height: '100%' }}>
        {renderLines()}
      </svg>
      {workflowSteps.map((step, index) => (
        <Draggable
          key={step}
          position={positions[step]}
          onDrag={(e, data) => handleDrag(e, data, step)}
        >
          <div
            onClick={() => handleClickStep(step)}
            style={{
              position: 'absolute',
              padding: '10px 20px',
              backgroundColor: '#f0f0f0',
              border: '1px solid #ccc',
              borderRadius: '8px',
              cursor: 'pointer',
              userSelect: 'none',
            }}
          >
            {step}
          </div>
        </Draggable>
      ))}
    </div>
  );
};

export default OutputPanel;
