import React from 'react';
import { useStateContext } from '../context/StateContext';
import { useNavigate } from 'react-router-dom';

const MessagePanel = () => {
  const { messages, normalizedTables, setSelectedTable, erDiagramGenerated } = useStateContext();
  const navigate = useNavigate();

  const handleTableClick = (tableName) => {
    setSelectedTable(tableName);
    navigate('/table-view');
  };

  const handleERDiagramClick = () => {
    navigate('/er-diagram');
  };

  // Helper function to remove file extension (like .csv)
  const stripExtension = (filename) => {
    return filename.split('.')[0];
  };

  return (
    <div className="message-panel">
      <h4>Messages</h4>
      <div className="messages-list">
        {messages.map((msg, index) => (
          <div key={index} className="message-item">
            {typeof msg === 'string' ? msg : JSON.stringify(msg)}
          </div>
        ))}
      </div>

      <div className="normalized-tables">
        
        {normalizedTables.map((table, index) => (
          <button key={index} onClick={() => handleTableClick(table)}>
            {stripExtension(table)}
          </button>
        ))}

        {erDiagramGenerated && (
          <button onClick={handleERDiagramClick} className="er-diagram-button">
            Show ER Diagram
          </button>
        )}
      </div>
    </div>
  );
};

export default MessagePanel;
