import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { TransformWrapper, TransformComponent } from 'react-zoom-pan-pinch';
import { useNavigate } from 'react-router-dom';

const ERDiagramPage = () => {
  const [imageURL, setImageURL] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchERDiagram = async () => {
      try {
        const response = await axios.get('/api/get_er_diagram_image', { responseType: 'blob' });
        const blobUrl = URL.createObjectURL(response.data);
        setImageURL(blobUrl);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load ER Diagram');
      }
    };

    fetchERDiagram();
  }, []);

  return (
    <div style={{ height: '100vh', width: '100%', overflow: 'hidden', backgroundColor: '#f8f9fa' }}>
      <div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          background: '#fff',
          padding: '10px',
          display: 'flex',
          justifyContent: 'center',
          gap: '10px',
          boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
          zIndex: 10,
        }}
      >
        <TransformWrapper initialScale={1} minScale={0.5} maxScale={5} wheel={{ step: 0.1 }}>
          {({ zoomIn, zoomOut, resetTransform }) => (
            <>
              <button onClick={zoomIn}>Zoom In</button>
              <button onClick={zoomOut}>Zoom Out</button>
              <button onClick={resetTransform}>Reset</button>
              <button onClick={() => navigate(-1)}>â¬… Back</button>
            </>
          )}
        </TransformWrapper>
      </div>

      <div style={{ paddingTop: '60px', height: 'calc(100% - 60px)' }}>
        {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
        {imageURL && (
          <TransformWrapper initialScale={1} minScale={0.5} maxScale={5} wheel={{ step: 0.1 }}>
            <TransformComponent>
              <img
                src={imageURL}
                alt="ER Diagram"
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'contain',
                }}
              />
            </TransformComponent>
          </TransformWrapper>
        )}
      </div>
    </div>
  );
};

export default ERDiagramPage;
