// src/App.jsx
import React, { useRef, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { StateProvider } from './context/StateContext';
import { Helmet } from 'react-helmet';
import {
  FaDatabase,
  FaTable,
  FaProjectDiagram,
  FaCode,
  FaCommentAlt,
  FaStream
} from 'react-icons/fa';

import ActionPanel from './components/ActionPanel';
import OutputPanel from './components/OutputPanel';
import CodePanel from './components/CodePanel';
import MessagePanel from './components/messagePanel';
import NormalizedTableView from './components/TableView';
import ERDiagramPage from './components/ERDiagramPage';
import FDPage from './components/FDPage'; // âœ… FD view

import './style.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

const PanelContainer = ({ children, title, icon }) => (
  <div className="panel-base">
    <div className="panel-header">
      <h3 className="panel-title">
        {icon && React.cloneElement(icon, { className: 'panel-icon' })}
        {title}
      </h3>
    </div>
    <div className="panel-content">{children}</div>
  </div>
);

const MainLayout = () => {
  const containerRef = useRef(null);
  const leftRef = useRef(null);
  const centerRef = useRef(null);
  const rightRef = useRef(null);
  const leftHandleRef = useRef(null);   // between left and center
  const rightHandleRef = useRef(null);  // between center and right

  useEffect(() => {
    const leftHandle = leftHandleRef.current;
    const rightHandle = rightHandleRef.current;
    const container = containerRef.current;
    const left = leftRef.current;
    const right = rightRef.current;

    if (!leftHandle || !rightHandle || !container || !left || !right) return;

    const LEFT_MIN = 150;
    const LEFT_MAX = 500;
    const RIGHT_MIN = 200;
    const RIGHT_MAX = 600;

    // Helper to disable text selection while dragging
    const startUIBlock = () => {
      document.body.style.userSelect = 'none';
      document.body.style.cursor = 'col-resize';
    };
    const stopUIBlock = () => {
      document.body.style.userSelect = '';
      document.body.style.cursor = '';
    };

    // LEFT HANDLE (adjust left panel width)
    const onLeftPointerDown = (ev) => {
      ev.preventDefault();
      startUIBlock();

      const onPointerMove = (e) => {
        const containerRect = container.getBoundingClientRect();
        let newLeftWidth = Math.round(e.clientX - containerRect.left);

        // Clamp
        newLeftWidth = Math.max(LEFT_MIN, Math.min(LEFT_MAX, newLeftWidth));

        left.style.width = `${newLeftWidth}px`;
      };

      const onPointerUp = () => {
        stopUIBlock();
        document.removeEventListener('pointermove', onPointerMove);
        document.removeEventListener('pointerup', onPointerUp);
      };

      document.addEventListener('pointermove', onPointerMove);
      document.addEventListener('pointerup', onPointerUp);
    };

    // RIGHT HANDLE (adjust right panel width)
    const onRightPointerDown = (ev) => {
      ev.preventDefault();
      startUIBlock();

      const onPointerMove = (e) => {
        const containerRect = container.getBoundingClientRect();
        // new right width = distance from mouse to right edge of container
        let newRightWidth = Math.round(containerRect.right - e.clientX);

        // Clamp
        newRightWidth = Math.max(RIGHT_MIN, Math.min(RIGHT_MAX, newRightWidth));

        right.style.width = `${newRightWidth}px`;
      };

      const onPointerUp = () => {
        stopUIBlock();
        document.removeEventListener('pointermove', onPointerMove);
        document.removeEventListener('pointerup', onPointerUp);
      };

      document.addEventListener('pointermove', onPointerMove);
      document.addEventListener('pointerup', onPointerUp);
    };

    leftHandle.addEventListener('pointerdown', onLeftPointerDown);
    rightHandle.addEventListener('pointerdown', onRightPointerDown);

    // cleanup
    return () => {
      leftHandle.removeEventListener('pointerdown', onLeftPointerDown);
      rightHandle.removeEventListener('pointerdown', onRightPointerDown);
    };
  }, []); // run once

  return (
    <div className="app-container" ref={containerRef}>
      <div className="left-panel" ref={leftRef}>
        <PanelContainer title="Actions" icon={<FaDatabase />}>
          <ActionPanel />
        </PanelContainer>
      </div>

      {/* Left resize handle */}
      <div
        className="resize-handle"
        ref={leftHandleRef}
        aria-label="Resize left and center panels"
        role="separator"
      />

      <div className="center-panel" ref={centerRef}>
        <PanelContainer title="Database Output" icon={<FaTable />}>
          <OutputPanel />
        </PanelContainer>
      </div>

      {/* Right resize handle */}
      <div
        className="resize-handle"
        ref={rightHandleRef}
        aria-label="Resize center and right panels"
        role="separator"
      />

      <div className="right-panel" ref={rightRef}>
        <PanelContainer title="Messages" icon={<FaCommentAlt />}>
          <MessagePanel />
        </PanelContainer>

        <PanelContainer title="Generated Code" icon={<FaCode />}>
          <CodePanel />
        </PanelContainer>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <StateProvider>
      <Helmet>
        <title>Database Design Studio</title>
        <meta name="description" content="Tool for creating database designs and ER diagrams" />
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="true" />
        <link
          href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&family=Fira+Code:wght@400;500&display=swap"
          rel="stylesheet"
        />
      </Helmet>

      <Router>
        <Routes>
          <Route path="/" element={<MainLayout />} />

          <Route
            path="/table-view"
            element={
              <PanelContainer title="Normalized Tables" icon={<FaTable />}>
                <NormalizedTableView />
              </PanelContainer>
            }
          />

          <Route
            path="/er-diagram"
            element={
              <PanelContainer title="ER Diagram" icon={<FaProjectDiagram />}>
                <ERDiagramPage />
              </PanelContainer>
            }
          />

          <Route
            path="/fd-view"
            element={
              <PanelContainer title="Detected Functional Dependencies" icon={<FaStream />}>
                <FDPage />
              </PanelContainer>
            }
          />
        </Routes>
      </Router>

      {/* SVG Arrowhead Definitions */}
      <svg className="svg-defs" style={{ display: 'none' }}>
        <defs>
          <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="9" refY="3.5" orient="auto">
            <polygon points="0 0, 10 3.5, 0 7" fill="#1a73e8" />
          </marker>
        </defs>
      </svg>
    </StateProvider>
  );
};

export default App;
