import React, { useEffect, useRef } from "react";
import { useStateContext } from "../context/StateContext";

const CodePanel = () => {
  const { codeSnippet } = useStateContext();
  const codeRef = useRef();

  useEffect(() => {
    if (codeRef.current) {
      codeRef.current.scrollTop = codeRef.current.scrollHeight;
    }
  }, [codeSnippet]);

  return (
    <div style={{
      background: "#1e1e1e",
      color: "#dcdcdc",
      padding: "10px",
      borderRadius: "8px",
      height: "400px",
      overflowY: "auto",
      fontFamily: "monospace",
      whiteSpace: "pre-wrap"
    }}
    ref={codeRef}>
      {codeSnippet || "// Click a workflow block to see the code..."}
    </div>
  );
};

export default CodePanel;
