import React, { useEffect } from 'react';
import { useStateContext } from '../context/StateContext';
import { useNavigate } from 'react-router-dom';

const MessagePanel = () => {
  const { messages, normalizedTables, fetchNormalizedTables, setSelectedTable } = useStateContext();
  const navigate = useNavigate();

  useEffect(() => {
    fetchNormalizedTables(); // Fetch table names on load
  }, []);

  const handleTableClick = (tableName) => {
    setSelectedTable(tableName);
    navigate('/table-view');
  };

  return (
    <div className="message-panel" style={{ padding: '10px', border: '1px solid #ccc', height: '100%', overflowY: 'auto', background: '#fafafa' }}>
      <h2>Message Panel</h2>
      <div>
        {messages.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>

      <h3>Normalized Tables</h3>
      <div>
        {normalizedTables.length > 0 ? (
          normalizedTables.map((table, index) => (
            <button
              key={index}
              onClick={() => handleTableClick(table)}
              style={{ display: 'block', margin: '5px 0', width: '100%' }}
            >
              {table}
            </button>
          ))
        ) : (
          <p>No tables available</p>
        )}
      </div>
    </div>
  );
};

export default MessagePanel;
