import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();

  const positionsInitialized = useRef(false);

  // Load saved positions BEFORE first render
  const initialPositions = (() => {
    try {
      const saved = localStorage.getItem(STORAGE_KEY);
      if (saved) {
        const parsed = JSON.parse(saved);
        positionsInitialized.current = true; // mark as initialized immediately
        return parsed;
      }
    } catch {
      console.warn('Failed to parse saved positions');
    }
    return {};
  })();

  const [positions, setPositions] = useState(initialPositions);
  const [boxWidths, setBoxWidths] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);
  const [controlPoints, setControlPoints] = useState({});
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const [currentStepIndex, setCurrentStepIndex] = useState(null);

  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  const stepDisplayNames = {
    Upload: 'Upload',
    CleanModify: 'Noise Clean',
    FDModified: 'FD Detection',
    NormalizeTable: 'Normalize to 3NF',
    LosslessCheck: 'Lossless Decomposition',
    ERDiagram: 'ER Diagram',
    ConvertToCSV: 'CSV Converter',
    KeyDetection: 'Key Detector',
    DependencyPreservation: 'Check Dependency Preservation',
  };

  // Initialize default positions only if no saved ones
  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;
    if (workflowSteps.length === 0) return;
    if (workflowSteps.some((step) => !boxWidths[step])) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPos = {};
    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      const width = boxWidths[step] || 140;
      if (x + width > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPos[step] = { x, y };
      x += width + horizontalSpacing;
    });

    setPositions(initialPos);
    positionsInitialized.current = true;
  }, [workflowSteps, boxWidths]);

  // Save positions to localStorage when changed
  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  // Keep currentStepIndex valid when steps change
  useEffect(() => {
    if (currentStepIndex == null) return;
    if (currentStepIndex >= workflowSteps.length) {
      setCurrentStepIndex(workflowSteps.length - 1);
    }
  }, [workflowSteps, currentStepIndex]);

  const handlePositionChange = (step, pos) => {
    setPositions((prev) => {
      setUndoStack((undoPrev) => [...undoPrev, prev]);
      setRedoStack([]);
      return { ...prev, [step]: pos };
    });
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
    const idx = workflowSteps.indexOf(step);
    if (idx !== -1) setCurrentStepIndex(idx);
  };

  const handleUndo = () => {
    if (undoStack.length === 0) return;
    setPositions((currentPositions) => {
      let newUndo = [...undoStack];
      let newRedo = [...redoStack, currentPositions];
      let prevPos;
      for (let i = 0; i < 2; i++) {
        if (newUndo.length === 0) break;
        prevPos = newUndo.pop();
        newRedo.push(prevPos);
      }
      setUndoStack(newUndo);
      setRedoStack(newRedo);
      return prevPos || currentPositions;
    });
  };

  const handleRedo = () => {
    if (redoStack.length === 0) return;
    setPositions((currentPositions) => {
      let newRedo = [...redoStack];
      let newUndo = [...undoStack, currentPositions];
      let nextPos;
      for (let i = 0; i < 2; i++) {
        if (newRedo.length === 0) break;
        nextPos = newRedo.pop();
        newUndo.push(nextPos);
      }
      setRedoStack(newRedo);
      setUndoStack(newUndo);
      return nextPos || currentPositions;
    });
  };

  function cubicBezierPoint(t, P0, P1, P2, P3) {
    const u = 1 - t;
    const tt = t * t;
    const uu = u * u;
    const uuu = uu * u;
    const ttt = tt * t;
    return {
      x: uuu * P0.x + 3 * uu * t * P1.x + 3 * u * tt * P2.x + ttt * P3.x,
      y: uuu * P0.y + 3 * uu * t * P1.y + 3 * u * tt * P2.y + ttt * P3.y,
    };
  }

  const renderLines = () => {
    const dotRadius = 4.5;
    const lines = [];
    const t1 = 0.25;
    const t2 = 0.75;

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];
      if (from && to) {
        const fromWidth = boxWidths[workflowSteps[i]] || 140;
        const padding = 6;
        const P0 = { x: from.x + fromWidth + padding, y: from.y + boxHeight / 2 };
        const P3 = { x: to.x - padding, y: to.y + boxHeight / 2 };
        const cp = controlPoints[i] || {};
        let P1, P2;
        if (cp.c1 && cp.c2) {
          P1 = cp.c1;
          P2 = cp.c2;
        } else {
          if (Math.abs(P3.x - P0.x) > Math.abs(P3.y - P0.y)) {
            P1 = { x: P0.x + (P3.x - P0.x) / 2, y: P0.y };
            P2 = { x: P0.x + (P3.x - P0.x) / 2, y: P3.y };
          } else {
            P1 = { x: P0.x, y: P0.y + (P3.y - P0.y) / 2 };
            P2 = { x: P3.x, y: P0.y + (P3.y - P0.y) / 2 };
          }
        }
        const dot1Pos = cubicBezierPoint(t1, P0, P1, P2, P3);
        const dot2Pos = cubicBezierPoint(t2, P0, P1, P2, P3);
        const d = `M${P0.x},${P0.y} C${P1.x},${P1.y} ${P2.x},${P2.y} ${P3.x},${P3.y}`;
        lines.push(
          <path
            key={`line-${i}`}
            d={d}
            fill="none"
            stroke="rgba(184, 212, 249, 0.9)"
            strokeWidth="1.5"
            markerEnd="url(#arrowhead)"
          />
        );
        lines.push(
          <circle
            key={`dot1-${i}`}
            cx={dot1Pos.x}
            cy={dot1Pos.y}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.9)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            pointerEvents="all"
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c1', e)}
          />,
          <circle
            key={`dot2-${i}`}
            cx={dot2Pos.x}
            cy={dot2Pos.y}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.9)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            pointerEvents="all"
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c2', e)}
          />
        );
      }
    }
    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  const draggingRef = useRef(null);

  const onMouseDownControlPoint = (lineIndex, pointKey, e) => {
    e.preventDefault();
    e.stopPropagation();
    draggingRef.current = { lineIndex, pointKey };
    window.addEventListener('mousemove', onMouseMoveControlPoint);
    window.addEventListener('mouseup', onMouseUpControlPoint);
  };

  const onMouseMoveControlPoint = (e) => {
    if (!draggingRef.current) return;
    const { lineIndex, pointKey } = draggingRef.current;
    const containerRect = containerRef.current.getBoundingClientRect();
    const mouseX = (e.clientX - containerRect.left) / scale;
    const mouseY = (e.clientY - containerRect.top) / scale;
    setControlPoints((prev) => ({
      ...prev,
      [lineIndex]: {
        ...prev[lineIndex],
        [pointKey]: { x: mouseX, y: mouseY },
      },
    }));
  };

  const onMouseUpControlPoint = () => {
    draggingRef.current = null;
    window.removeEventListener('mousemove', onMouseMoveControlPoint);
    window.removeEventListener('mouseup', onMouseUpControlPoint);
  };

  const handleSizeChange = (step, width) => {
    setBoxWidths((prev) => {
      if (prev[step] === width) return prev;
      return { ...prev, [step]: width };
    });
  };

  const goToIndex = (idx) => {
    if (!containerRef.current) return;
    if (!positionsInitialized.current) return;
    if (idx == null || idx < 0 || idx >= workflowSteps.length) return;
    const step = workflowSteps[idx];
    const pos = positions[step];
    if (!pos) return;
    const boxW = boxWidths[step] || 140;
    const container = containerRef.current;
    const containerWidth = container.clientWidth;
    const containerHeight = container.clientHeight;
    const targetLeft = Math.round(pos.x * scale - containerWidth / 2 + (boxW * scale) / 2);
    const targetTop = Math.round(pos.y * scale - containerHeight / 2 + (boxHeight * scale) / 2);
    container.scrollTo({
      left: Math.max(0, targetLeft),
      top: Math.max(0, targetTop),
      behavior: 'smooth',
    });
    setCurrentStepIndex(idx);
    handleClickStep(step);
  };

  const prevStep = () => {
    if (workflowSteps.length === 0) return;
    const start = currentStepIndex == null ? workflowSteps.length - 1 : currentStepIndex - 1;
    goToIndex(Math.max(0, start));
  };

  const nextStep = () => {
    if (workflowSteps.length === 0) return;
    const start = currentStepIndex == null ? 0 : currentStepIndex + 1;
    goToIndex(Math.min(workflowSteps.length - 1, start));
  };

  return (
    <div
      className="output-panel"
      style={{ width: '100%', height: '100%', position: 'relative', overflow: 'auto', padding: 20 }}
      ref={containerRef}
    >
      {/* Controls */}
      <div style={{ position: 'absolute', top: 5, right: 5, zIndex: 5, display: 'flex', gap: 6 }}>
        <button onClick={zoomIn} title="Zoom in">+</button>
        <button onClick={zoomOut} title="Zoom out">−</button>
        <button onClick={handleUndo} disabled={undoStack.length === 0} title="Undo (2 steps)">↺</button>
        <button onClick={handleRedo} disabled={redoStack.length === 0} title="Redo (2 steps)">↻</button>
        <button onClick={prevStep} disabled={!positionsInitialized.current || workflowSteps.length === 0} title="Step backward">◀</button>
        <button onClick={nextStep} disabled={!positionsInitialized.current || workflowSteps.length === 0} title="Step forward">▶</button>
      </div>

      {/* Canvas */}
      <div
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          position: 'relative',
          minHeight: '100%',
          width: '100%',
        }}
      >
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none',
            overflow: 'visible',
            top: 0,
            left: 0,
          }}
        >
          <defs>
            <marker id="arrowhead" markerWidth="10" markerHeight="7" refX="10" refY="3.5" orient="auto" fill="rgba(103, 121, 136, 0.9)">
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            displayName={stepDisplayNames[step] || step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
            onSizeChange={handleSizeChange}
            isActive={index === currentStepIndex}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
