import React from "react";
import { useStateContext } from "../context/StateContext";

export default function MessagePanel() {
  const { messages, normalizedTables, selectTable, selectedTable } = useStateContext();

  return (
    <div style={{ border: "1px solid #ccc", padding: 10, marginBottom: 10 }}>
      <h3>Message Panel</h3>
      <div style={{ maxHeight: 100, overflowY: "auto", marginBottom: 10 }}>
        {messages.map((msg, i) => (
          <div key={i} style={{ fontSize: 12 }}>
            {msg}
          </div>
        ))}
      </div>
      {normalizedTables.length > 0 && (
        <>
          <div>Normalized Tables:</div>
          {normalizedTables.map((tbl) => (
            <button
              key={tbl}
              onClick={() => selectTable(tbl)}
              style={{
                marginRight: 5,
                marginTop: 5,
                backgroundColor: tbl === selectedTable ? "#aaf" : "#eee",
              }}
            >
              {tbl}
            </button>
          ))}
        </>
      )}
    </div>
  );
}
