import React, { useRef, useEffect } from "react";

const DraggableButton = ({ id, label, position, onDrag, onClick }) => {
  const btnRef = useRef();

  useEffect(() => {
    const btn = btnRef.current;
    const handleMouseDown = (e) => {
      const shiftX = e.clientX - btn.getBoundingClientRect().left;
      const shiftY = e.clientY - btn.getBoundingClientRect().top;

      const onMouseMove = (e) => {
        onDrag(id, { x: e.clientX - shiftX, y: e.clientY - shiftY });
      };

      document.addEventListener("mousemove", onMouseMove);
      document.addEventListener("mouseup", () => {
        document.removeEventListener("mousemove", onMouseMove);
      }, { once: true });
    };

    btn.addEventListener("mousedown", handleMouseDown);

    return () => {
      btn.removeEventListener("mousedown", handleMouseDown);
    };
  }, [id, onDrag]);

  return (
    <div
      ref={btnRef}
      onClick={() => onClick(id)}
      style={{
        position: "absolute",
        left: position.x,
        top: position.y,
        cursor: "pointer",
        padding: "10px",
        backgroundColor: "#3a4b73",
        color: "white",
        borderRadius: "8px",
        userSelect: "none",
      }}
    >
      {label}
    </div>
  );
};

export default DraggableButton;
