import axios from 'axios';

// Backend Base URL (adjust if needed)
const BASE_URL = 'http://localhost:5000';

// Fetch list of normalized tables (for dropdown in MessagePanel)
export async function fetchNormalizedTables() {
  try {
    const res = await axios.get(`${BASE_URL}/api/normalized_tables`);
    return res.data.tables;  // Should return array like ["table_1", "table_2"]
  } catch (err) {
    console.error('Error fetching normalized tables:', err);
    return [];
  }
}

// Fetch code snippet for a workflow step (for CodePanel)
export async function fetchCodeForStep(stepName) {
  try {
    const res = await axios.get(`${BASE_URL}/api/code/${stepName}`);
    return res.data.code;  // Should return a string containing Python code
  } catch (err) {
    console.error(`Error fetching code for step ${stepName}:`, err);
    return "Error fetching code snippet.";
  }
}

// Validate whether adding a step is allowed (order checking)
export const validateStep = async (currentSteps, nextStep) => {
  try {
    const response = await fetch('/api/validate_step', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ currentSteps, nextStep }),
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const result = await response.json();
    return result;
  } catch (error) {
    console.error("Validation API error:", error);
    return { valid: false, message: "Error validating step." };
  }
};


// Fetch the HTML content for a selected table (for MessagePanel)
export async function fetchTableHTML(tableName) {
  try {
    const res = await axios.get(`${BASE_URL}/api/table_html/${tableName}`);
    return res.data.html;  // Should return raw HTML string
  } catch (err) {
    console.error(`Error fetching table HTML for ${tableName}:`, err);
    return "<p>Error loading table.</p>";
  }
}
