import React, { useEffect, useRef } from 'react';
import { useDrag } from '@use-gesture/react';

const DraggableStep = ({
  step,
  position,
  onPositionChange,
  onDragEnd,
  onClick,
  isFirst,
  displayName,
  onSizeChange,
  isActive,
}) => {
  const ref = useRef(null);

  useEffect(() => {
    if (ref.current) {
      const width = ref.current.offsetWidth;
      onSizeChange && onSizeChange(step, width);
    }
  }, [step, onSizeChange]);

  const bind = useDrag(({ offset: [x, y], last }) => {
    onPositionChange(step, { x, y });
    if (last) {
      onDragEnd && onDragEnd(step, { x, y });
    }
  });

  return (
    <div
      {...bind()}
      ref={ref}
      onClick={() => onClick(step)}
      className={`draggable-node ${isActive ? 'active' : ''}`}
    >
      {!isFirst && <div className="connection-dot left-dot" />}
      <div className="step-label">{displayName || step}</div>
      <div className="connection-dot right-dot" />
    </div>
  );
};


export default DraggableStep;
