import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setWorkflowSteps, setCurrentCode } = useStateContext(); // Added setWorkflowSteps
  const [positions, setPositions] = useState({});
  const [boxWidths, setBoxWidths] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  const [controlPoints, setControlPoints] = useState({});
  const positionsInitialized = useRef(false);

  // Undo / Redo stacks now store BOTH positions and workflowSteps
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  const stepDisplayNames = {
    Upload: 'Upload',
    CleanModify: 'Noise Clean',
    FDModified: 'FD Detection',
    NormalizeTable: 'Normalize to 3NF',
    LosslessCheck: 'Lossless Decomposition',
    ERDiagram: 'ER Diagram',
    ConvertToCSV: 'CSV Converter',
    KeyDetection: 'Key Detector',
    DependencyPreservation: 'Check Dependency Preservation',
  };

  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        const parsedPositions = JSON.parse(savedPositions);
        setPositions(parsedPositions);
        positionsInitialized.current = true;
      } catch {
        console.warn('Failed to parse saved positions');
      }
    }
  }, []);

  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;
    if (workflowSteps.length === 0) return;
    if (workflowSteps.some((step) => !boxWidths[step])) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};

    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      const width = boxWidths[step] || 140;

      if (x + width > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += width + horizontalSpacing;
    });

    setPositions((prev) => ({ ...prev, ...initialPositions }));
    positionsInitialized.current = true;
  }, [workflowSteps, boxWidths]);

  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  // Save BOTH positions and workflow steps in undo stack
  const saveHistory = (pos, steps) => {
    setUndoStack((undoPrev) => [...undoPrev, { positions: pos, steps }]);
    setRedoStack([]);
  };

  const handlePositionChange = (step, pos) => {
    saveHistory(positions, workflowSteps);
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  const handleUndo = () => {
    if (undoStack.length === 0) return;

    const lastState = undoStack[undoStack.length - 1];
    const newUndo = undoStack.slice(0, -1);
    setRedoStack((r) => [...r, { positions, steps: workflowSteps }]);
    setPositions(lastState.positions);
    setWorkflowSteps(lastState.steps);
    setUndoStack(newUndo);
  };

  const handleRedo = () => {
    if (redoStack.length === 0) return;

    const lastState = redoStack[redoStack.length - 1];
    const newRedo = redoStack.slice(0, -1);
    setUndoStack((u) => [...u, { positions, steps: workflowSteps }]);
    setPositions(lastState.positions);
    setWorkflowSteps(lastState.steps);
    setRedoStack(newRedo);
  };

  function cubicBezierPoint(t, P0, P1, P2, P3) {
    const u = 1 - t;
    const tt = t * t;
    const uu = u * u;
    const uuu = uu * u;
    const ttt = tt * t;

    const x =
      uuu * P0.x +
      3 * uu * t * P1.x +
      3 * u * tt * P2.x +
      ttt * P3.x;
    const y =
      uuu * P0.y +
      3 * uu * t * P1.y +
      3 * u * tt * P2.y +
      ttt * P3.y;

    return { x, y };
  }

  const renderLines = () => {
    const dotRadius = 4.5;
    const lines = [];

    const t1 = 0.25;
    const t2 = 0.75;

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];

      if (from && to) {
        const fromWidth = boxWidths[workflowSteps[i]] || 140;
        const padding = 6;

        const P0 = {
          x: from.x + fromWidth + padding,
          y: from.y + boxHeight / 2,
        };

        const P3 = {
          x: to.x - padding,
          y: to.y + boxHeight / 2,
        };

        const cp = controlPoints[i] || {};
        let P1, P2;

        if (cp.c1 && cp.c2) {
          P1 = cp.c1;
          P2 = cp.c2;
        } else {
          if (Math.abs(P3.x - P0.x) > Math.abs(P3.y - P0.y)) {
            P1 = { x: P0.x + (P3.x - P0.x) / 2, y: P0.y };
            P2 = { x: P0.x + (P3.x - P0.x) / 2, y: P3.y };
          } else {
            P1 = { x: P0.x, y: P0.y + (P3.y - P0.y) / 2 };
            P2 = { x: P3.x, y: P0.y + (P3.y - P0.y) / 2 };
          }
        }

        const dot1Pos = cubicBezierPoint(t1, P0, P1, P2, P3);
        const dot2Pos = cubicBezierPoint(t2, P0, P1, P2, P3);

        const d = `M${P0.x},${P0.y} C${P1.x},${P1.y} ${P2.x},${P2.y} ${P3.x},${P3.y}`;

        lines.push(
          <path
            key={`line-${i}`}
            d={d}
            fill="none"
            stroke="rgba(184, 212, 249, 0.9)"
            strokeWidth="1.5"
            markerEnd="url(#arrowhead)"
          />
        );

        lines.push(
          <circle
            key={`dot1-${i}`}
            cx={dot1Pos.x}
            cy={dot1Pos.y}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.9)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            pointerEvents="all"
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c1', e)}
          />,
          <circle
            key={`dot2-${i}`}
            cx={dot2Pos.x}
            cy={dot2Pos.y}
            r={dotRadius}
            fill="rgba(70, 130, 180, 0.9)"
            stroke="#333"
            strokeWidth="1.5"
            style={{ cursor: 'grab' }}
            pointerEvents="all"
            onMouseDown={(e) => onMouseDownControlPoint(i, 'c2', e)}
          />
        );
      }
    }
    return lines;
  };

  const zoomIn = () => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)));
  const zoomOut = () => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)));

  const draggingRef = useRef(null);

  const onMouseDownControlPoint = (lineIndex, pointKey, e) => {
    e.preventDefault();
    e.stopPropagation();
    draggingRef.current = { lineIndex, pointKey };

    window.addEventListener('mousemove', onMouseMoveControlPoint);
    window.addEventListener('mouseup', onMouseUpControlPoint);
  };

  const onMouseMoveControlPoint = (e) => {
    if (!draggingRef.current) return;

    const { lineIndex, pointKey } = draggingRef.current;
    const containerRect = containerRef.current.getBoundingClientRect();
    const mouseX = (e.clientX - containerRect.left) / scale;
    const mouseY = (e.clientY - containerRect.top) / scale;

    setControlPoints((prev) => ({
      ...prev,
      [lineIndex]: {
        ...prev[lineIndex],
        [pointKey]: { x: mouseX, y: mouseY },
      },
    }));
  };

  const onMouseUpControlPoint = () => {
    draggingRef.current = null;
    window.removeEventListener('mousemove', onMouseMoveControlPoint);
    window.removeEventListener('mouseup', onMouseUpControlPoint);
  };

  const handleSizeChange = (step, width) => {
    setBoxWidths((prev) => {
      if (prev[step] === width) return prev;
      return { ...prev, [step]: width };
    });
  };

  return (
    <div
      className="output-panel"
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'auto',
        padding: 20,
      }}
      ref={containerRef}
    >
      <div
        className="zoom-controls"
        style={{
          position: 'absolute',
          top: 5,
          right: 5,
          zIndex: 5,
          display: 'flex',
          gap: 6,
          alignItems: 'center',
        }}
      >
        <button onClick={zoomIn} title="Zoom in">+</button>
        <button onClick={zoomOut} title="Zoom out">−</button>
        <button
          onClick={handleUndo}
          disabled={undoStack.length === 0}
          title="Undo"
        >
          ↺
        </button>
        <button
          onClick={handleRedo}
          disabled={redoStack.length === 0}
          title="Redo"
        >
          ↻
        </button>
      </div>

      <div
        className="canvas-wrapper"
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          position: 'relative',
          minHeight: '100%',
          width: '100%',
        }}
      >
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none',
            overflow: 'visible',
            top: 0,
            left: 0,
          }}
        >
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              fill="rgba(103, 121, 136, 0.9)"
            >
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            displayName={stepDisplayNames[step] || step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
            onSizeChange={handleSizeChange}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
