import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { TransformWrapper, TransformComponent } from 'react-zoom-pan-pinch';

const ERDiagramPage = () => {
  const [imageURL, setImageURL] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchERDiagram = async () => {
      try {
        const response = await axios.get('/api/get_er_diagram_image', { responseType: 'blob' });
        const blobUrl = URL.createObjectURL(response.data);
        setImageURL(blobUrl);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load ER Diagram');
      }
    };

    fetchERDiagram();
  }, []);

  return (
    <div style={{ height: '90vh', width: '100%', overflow: 'hidden', backgroundColor: '#66686aff' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '10px' }}>ER Diagram</h2>
      {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
      {imageURL && (
        <TransformWrapper
          initialScale={1}
          minScale={0.5}
          maxScale={5}
          wheel={{ step: 0.1 }}
        >
          {({ zoomIn, zoomOut, resetTransform }) => (
            <>
              <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginBottom: '5px' }}>
                <button onClick={() => zoomIn()}>Zoom In</button>
                <button onClick={() => zoomOut()}>Zoom Out</button>
                <button onClick={() => resetTransform()}>Reset</button>
              </div>
              <TransformComponent>
                <img
                  src={imageURL}
                  alt="ER Diagram"
                  style={{ maxWidth: '100%', maxHeight: '100%' }}
                />
              </TransformComponent>
            </>
          )}
        </TransformWrapper>
      )}
    </div>
  );
};

export default ERDiagramPage;
