import React, { useEffect, useRef, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { StateProvider } from './context/StateContext';
import { Helmet } from 'react-helmet';
import {
  FaDatabase,
  FaTable,
  FaProjectDiagram,
  FaCode,
  FaCommentAlt,
  FaStream
} from 'react-icons/fa';

import ActionPanel from './components/ActionPanel';
import OutputPanel from './components/OutputPanel';
import CodePanel from './components/CodePanel';
import MessagePanel from './components/messagePanel';
import NormalizedTableView from './components/TableView';
import ERDiagramPage from './components/ERDiagramPage';
import FDPage from './components/FDPage';

import './style.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

const PanelContainer = ({ children, title, icon }) => (
  <div className="panel-base">
    <div className="panel-header">
      <h3 className="panel-title">
        {icon && React.cloneElement(icon, { className: 'panel-icon' })}
        {title}
      </h3>
    </div>
    <div className="panel-content">{children}</div>
  </div>
);

const MainLayout = () => {
  // Panel refs
  const leftRef = useRef(null);
  const centerRef = useRef(null);
  const rightRef = useRef(null);
  const messagesRef = useRef(null);
  const codeRef = useRef(null);

  // Resize handle refs instead of document.querySelector
  const leftCenterHandleRef = useRef(null);
  const centerRightHandleRef = useRef(null);
  const verticalHandleRef = useRef(null);

  // State for widths & heights with localStorage fallback
  const [leftWidth, setLeftWidth] = useState(() => {
    const saved = localStorage.getItem('panel-left-width');
    return saved ? parseInt(saved, 10) : 240;
  });
  const [centerWidth, setCenterWidth] = useState(() => {
    const saved = localStorage.getItem('panel-center-width');
    return saved ? parseInt(saved, 10) : 600;
  });
  const [rightWidth, setRightWidth] = useState(() => {
    const saved = localStorage.getItem('panel-right-width');
    return saved ? parseInt(saved, 10) : 240;
  });

  const [messagesHeight, setMessagesHeight] = useState(() => {
    const saved = localStorage.getItem('panel-messages-height');
    return saved ? parseInt(saved, 10) : 150;
  });
  const [codeHeight, setCodeHeight] = useState(() => {
    const saved = localStorage.getItem('panel-code-height');
    return saved ? parseInt(saved, 10) : 240;
  });

  useEffect(() => {
    const minLeftWidth = 150;
    const minCenterWidth = 200;
    const minRightWidth = 200;

    const minMessagesHeight = 80;
    const minCodeHeight = 80;

    // Horizontal resize logic for left-center and center-right
    function setupHorizontalResize(handle, leftEl, rightEl, minLeft, minRight, setLeftSize, setRightSize, leftKey, rightKey) {
      let startX = 0;
      let startLeftWidth = 0;
      let startRightWidth = 0;

      function onMouseMove(e) {
        const dx = e.clientX - startX;
        let newLeftWidth = startLeftWidth + dx;
        let newRightWidth = startRightWidth - dx;

        if (newLeftWidth < minLeft) {
          newLeftWidth = minLeft;
          newRightWidth = startLeftWidth + startRightWidth - minLeft;
        } else if (newRightWidth < minRight) {
          newRightWidth = minRight;
          newLeftWidth = startLeftWidth + startRightWidth - minRight;
        }

        if (leftEl) leftEl.style.flex = `0 0 ${newLeftWidth}px`;
        if (rightEl) rightEl.style.flex = `0 0 ${newRightWidth}px`;
      }

      function onMouseUp() {
        document.removeEventListener('mousemove', onMouseMove);
        document.removeEventListener('mouseup', onMouseUp);

        if (!leftEl || !rightEl) return;

        const leftW = Math.round(leftEl.getBoundingClientRect().width);
        const rightW = Math.round(rightEl.getBoundingClientRect().width);

        setLeftSize(leftW);
        setRightSize(rightW);
        localStorage.setItem(leftKey, leftW);
        localStorage.setItem(rightKey, rightW);
      }

      function onMouseDown(e) {
        e.preventDefault();
        startX = e.clientX;
        if (!leftEl || !rightEl) return;
        startLeftWidth = leftEl.getBoundingClientRect().width;
        startRightWidth = rightEl.getBoundingClientRect().width;

        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
      }

      if (handle) {
        handle.addEventListener('mousedown', onMouseDown);
      }

      return () => {
        if (handle) {
          handle.removeEventListener('mousedown', onMouseDown);
        }
      };
    }

    // Vertical resize logic for messages-code panels
    function setupVerticalResize(handle, topEl, bottomEl, minTop, minBottom, setTopSize, setBottomSize, topKey, bottomKey) {
      let startY = 0;
      let startTopHeight = 0;
      let startBottomHeight = 0;

      function onMouseMove(e) {
        const dy = e.clientY - startY;
        let newTopHeight = startTopHeight + dy;
        let newBottomHeight = startBottomHeight - dy;

        if (newTopHeight < minTop) {
          newTopHeight = minTop;
          newBottomHeight = startTopHeight + startBottomHeight - minTop;
        } else if (newBottomHeight < minBottom) {
          newBottomHeight = minBottom;
          newTopHeight = startTopHeight + startBottomHeight - minBottom;
        }

        if (topEl) topEl.style.flex = `0 0 ${newTopHeight}px`;
        if (bottomEl) bottomEl.style.flex = `0 0 ${newBottomHeight}px`;
      }

      function onMouseUp() {
        document.removeEventListener('mousemove', onMouseMove);
        document.removeEventListener('mouseup', onMouseUp);

        if (!topEl || !bottomEl) return;

        const topH = Math.round(topEl.getBoundingClientRect().height);
        const bottomH = Math.round(bottomEl.getBoundingClientRect().height);

        setTopSize(topH);
        setBottomSize(bottomH);
        localStorage.setItem(topKey, topH);
        localStorage.setItem(bottomKey, bottomH);
      }

      function onMouseDown(e) {
        e.preventDefault();
        startY = e.clientY;
        if (!topEl || !bottomEl) return;
        startTopHeight = topEl.getBoundingClientRect().height;
        startBottomHeight = bottomEl.getBoundingClientRect().height;

        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', onMouseUp);
      }

      if (handle) {
        handle.addEventListener('mousedown', onMouseDown);
      }

      return () => {
        if (handle) {
          handle.removeEventListener('mousedown', onMouseDown);
        }
      };
    }

    const cleanups = [];

    if (leftCenterHandleRef.current && leftRef.current && centerRef.current) {
      cleanups.push(
        setupHorizontalResize(
          leftCenterHandleRef.current,
          leftRef.current,
          centerRef.current,
          minLeftWidth,
          minCenterWidth,
          setLeftWidth,
          setCenterWidth,
          'panel-left-width',
          'panel-center-width'
        )
      );
    }

    if (centerRightHandleRef.current && centerRef.current && rightRef.current) {
      cleanups.push(
        setupHorizontalResize(
          centerRightHandleRef.current,
          centerRef.current,
          rightRef.current,
          minCenterWidth,
          minRightWidth,
          setCenterWidth,
          setRightWidth,
          'panel-center-width',
          'panel-right-width'
        )
      );
    }

    if (verticalHandleRef.current && messagesRef.current && codeRef.current) {
      cleanups.push(
        setupVerticalResize(
          verticalHandleRef.current,
          messagesRef.current,
          codeRef.current,
          minMessagesHeight,
          minCodeHeight,
          setMessagesHeight,
          setCodeHeight,
          'panel-messages-height',
          'panel-code-height'
        )
      );
    }

    // Cleanup on unmount
    return () => {
      cleanups.forEach((cleanup) => cleanup && cleanup());
    };
  }, []);

  // Apply widths and heights on changes
  useEffect(() => {
    if (leftRef.current) leftRef.current.style.flex = `0 0 ${leftWidth}px`;
  }, [leftWidth]);

  useEffect(() => {
    if (centerRef.current) centerRef.current.style.flex = `0 0 ${centerWidth}px`;
  }, [centerWidth]);

  useEffect(() => {
    if (rightRef.current) rightRef.current.style.flex = `0 0 ${rightWidth}px`;
  }, [rightWidth]);

  useEffect(() => {
    if (messagesRef.current) messagesRef.current.style.flex = `0 0 ${messagesHeight}px`;
  }, [messagesHeight]);

  useEffect(() => {
    if (codeRef.current) codeRef.current.style.flex = `0 0 ${codeHeight}px`;
  }, [codeHeight]);

  return (
    <div className="app-container">
      {/* Left panel */}
      <div
        className="left-panel"
        ref={leftRef}
        style={{ flex: `0 0 ${leftWidth}px` }}
      >
        <PanelContainer title="Actions" icon={<FaDatabase />}>
          <ActionPanel />
        </PanelContainer>
      </div>

      {/* Horizontal resize handle between left and center */}
      <div className="resize-handle-x left-center" ref={leftCenterHandleRef} />

      {/* Center panel */}
      <div
        className="center-panel"
        ref={centerRef}
        style={{ flex: `0 0 ${centerWidth}px` }}
      >
        <PanelContainer title="Database Output" icon={<FaTable />}>
          <OutputPanel />
        </PanelContainer>
      </div>

      {/* Horizontal resize handle between center and right */}
      <div className="resize-handle-x center-right" ref={centerRightHandleRef} />

      {/* Right panel */}
      <div
        className="right-panel"
        ref={rightRef}
        style={{
          flex: `0 0 ${rightWidth}px`,
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        {/* Messages panel with vertical resize */}
        <div
          ref={messagesRef}
          style={{
            flex: `0 0 ${messagesHeight}px`,
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
          }}
        >
          <PanelContainer title="Messages" icon={<FaCommentAlt />}>
            <MessagePanel />
          </PanelContainer>
        </div>

        {/* Vertical resize handle between messages and code */}
        <div className="resize-handle-y messages-code" ref={verticalHandleRef} />

        {/* Code panel */}
        <div
          ref={codeRef}
          style={{
            flex: `0 0 ${codeHeight}px`,
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
          }}
        >
          <PanelContainer title="Generated Code" icon={<FaCode />}>
            <CodePanel />
          </PanelContainer>
        </div>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <StateProvider>
      <Helmet>
        <title>Database Design Studio</title>
        <meta
          name="description"
          content="Tool for creating database designs and ER diagrams"
        />
        <link rel="preconnect" href="https://fonts.googleapis.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="true" />
        <link
          href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&family=Fira+Code:wght@400;500&display=swap"
          rel="stylesheet"
        />
      </Helmet>

      <Router>
        <Routes>
          <Route path="/" element={<MainLayout />} />
          <Route
            path="/table-view"
            element={
              <PanelContainer title="Normalized Tables" icon={<FaTable />}>
                <NormalizedTableView />
              </PanelContainer>
            }
          />
          <Route
            path="/er-diagram"
            element={
              <PanelContainer title="ER Diagram" icon={<FaProjectDiagram />}>
                <ERDiagramPage />
              </PanelContainer>
            }
          />
          <Route
            path="/fd-view"
            element={
              <PanelContainer
                title="Detected Functional Dependencies"
                icon={<FaStream />}
              >
                <FDPage />
              </PanelContainer>
            }
          />
        </Routes>
      </Router>

      {/* SVG Arrowhead Definitions */}
      <svg className="svg-defs">
        <defs>
          <marker
            id="arrowhead"
            markerWidth="10"
            markerHeight="7"
            refX="9"
            refY="3.5"
            orient="auto"
          >
            <polygon points="0 0, 10 3.5, 0 7" fill="#1a73e8" />
          </marker>
        </defs>
      </svg>
    </StateProvider>
  );
};

export default App;
