import React from "react";
import { useStateContext } from "../context/StateContext";

/**
 * CodePanel displays the current code snippet or a placeholder message.
 * It handles large text with scroll and word wrapping for readability.
 */
const CodePanel = () => {
  const { currentCode } = useStateContext();

  const styles = {
    container: {
      overflowY: "auto",
      height: "100%",
      padding: 10,
      backgroundColor: "#3c3c3cff", // optional dark background for code panel
      color: "#d4d4d4", // light text for contrast
      fontFamily: "'Source Code Pro', monospace", // code font
      fontSize: 14,
      borderRadius: 4,
      boxSizing: "border-box",
      userSelect: "text",
      whiteSpace: "pre-wrap",
      wordBreak: "break-word",
      outline: "none",
    },
  };

  return (
    <section
      className="code-panel"
      style={styles.container}
      aria-label="Code display panel"
      tabIndex={0} // keeps keyboard focusable for accessibility
    >
      <pre>{currentCode || "Click a button in Output Panel to see code here."}</pre>
    </section>
  );
};

export default CodePanel;
