import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';

const ERDiagramPage = () => {
  const [imageURL, setImageURL] = useState(null);
  const [error, setError] = useState(null);
  const containerRef = useRef(null);
  const scaleRef = useRef(1); // store current scale

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    const onWheel = (e) => {
      if (!e.ctrlKey) return; // zoom only if Ctrl pressed
      e.preventDefault();

      const delta = e.deltaY > 0 ? -0.1 : 0.1;
      scaleRef.current = Math.min(Math.max(0.5, scaleRef.current + delta), 3);

      container.style.transform = `scale(${scaleRef.current})`;
    };

    container.addEventListener('wheel', onWheel, { passive: false });

    return () => {
      container.removeEventListener('wheel', onWheel);
    };
  }, []);

  const resetZoom = () => {
    const container = containerRef.current;
    if (!container) return;

    scaleRef.current = 1;
    container.style.transform = 'scale(1)';
  };

  const goBack = () => {
    window.history.back();
  };

  useEffect(() => {
    const fetchERDiagram = async () => {
      try {
        const response = await axios.get('/api/get_er_diagram_image', { responseType: 'blob' });
        const blobUrl = URL.createObjectURL(response.data);
        setImageURL(blobUrl);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load ER Diagram');
      }
    };

    fetchERDiagram();
  }, []);

  return (
    <div>
      <h2>ER Diagram</h2>

      <div
        style={{
          position: 'fixed',
          top: 10,
          backgroundColor: 'rgba(255, 255, 255, 0)',
          padding: '8px 12px',
          borderRadius: 4,
          boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
          zIndex: 1000,
          display: 'flex',
          gap: '8px',
        }}
      >
        <button onClick={resetZoom}>Reset Zoom</button>
        <button onClick={goBack}>Back</button>
      </div>

      {error && <div style={{ color: 'red', marginTop: 48 }}>{error}</div>}

      <div
        ref={containerRef}
        style={{
          border: '1px solid #ccc',
          overflow: 'auto',
          width: '100%',
          height: '80vh',
          cursor: 'grab',
          transformOrigin: '0 0',
          marginTop: '16px',
        }}
      >
        {imageURL && (
          <img
            src={imageURL}
            alt="ER Diagram"
            style={{ width: '100%', height: 'auto', display: 'block' }}
            draggable={false}
          />
        )}
      </div>

      <p style={{ fontSize: '0.9rem', color: '#666', marginTop: 12 }}>
        Use <strong>Ctrl + Mouse Wheel</strong> to zoom. Drag to pan.
      </p>
    </div>
  );
};

export default ERDiagramPage;
