import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';

const ERDiagramPage = () => {
  const [imageURL, setImageURL] = useState(null);
  const [error, setError] = useState(null);
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    let scale = 1;

    const onWheel = (e) => {
      if (!e.ctrlKey) return; // zoom only when Ctrl key is pressed
      e.preventDefault();

      const delta = e.deltaY > 0 ? -0.1 : 0.1;
      scale = Math.min(Math.max(0.5, scale + delta), 3);

      container.style.transform = `scale(${scale})`;
    };

    container.addEventListener('wheel', onWheel, { passive: false });

    return () => {
      container.removeEventListener('wheel', onWheel);
    };
  }, []);

  useEffect(() => {
    const fetchERDiagram = async () => {
      try {
        const response = await axios.get('/api/get_er_diagram_image', { responseType: 'blob' });
        const blobUrl = URL.createObjectURL(response.data);
        setImageURL(blobUrl);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load ER Diagram');
      }
    };

    fetchERDiagram();
  }, []);

  return (
    <div>
      <h2>ER Diagram</h2>

      <button
        onClick={() => window.history.back()}
        style={{
          marginBottom: '10px',
          padding: '8px 16px',
          cursor: 'pointer',
          borderRadius: '4px',
          border: '1px solid #888',
          backgroundColor: '#f0f0f0',
          fontSize: '1rem',
        }}
      >
        ‚Üê Back
      </button>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <div
        ref={containerRef}
        style={{
          border: '1px solid #ccc',
          overflow: 'auto',
          width: '100%',
          height: '80vh',
          cursor: 'grab',
          transformOrigin: '0 0',
        }}
      >
        {imageURL && (
          <img
            src={imageURL}
            alt="ER Diagram"
            style={{ width: '100%', height: 'auto', display: 'block' }}
            draggable={false}
          />
        )}
      </div>

      <p style={{ fontSize: '0.9rem', color: '#666' }}>
        Use <strong>Ctrl + Mouse Wheel</strong> to zoom. Drag scrollbars or touchpad to pan.
      </p>
    </div>
  );
};

export default ERDiagramPage;
