import React, { createContext, useContext, useState, useEffect } from "react";
import {
  fetchNormalizedTables,
  fetchCodeForStep,
  fetchTableHTML,
  validateStep,
} from "../api";

const StateContext = createContext();

export const StateProvider = ({ children }) => {
  const [workflowSteps, setWorkflowSteps] = useState([]);
  const [codeSnippet, setCodeSnippet] = useState("");
  const [messages, setMessages] = useState([]);
  const [normalizedTables, setNormalizedTables] = useState([]);
  const [selectedTableHTML, setSelectedTableHTML] = useState("");

  useEffect(() => {
    async function loadTables() {
      try {
        const tables = await fetchNormalizedTables();
        setNormalizedTables(tables);
      } catch (err) {
        console.error("Error fetching tables:", err);
      }
    }
    loadTables();
  }, []);

  const addStep = async (stepName) => {
    const res = await validateStep(workflowSteps, stepName);
    if (res.valid) {
      setWorkflowSteps((prev) => [...prev, stepName]);
      setMessages((prev) => [...prev, `âœ… Step "${stepName}" added to workflow.`]);
      const code = await fetchCodeForStep(stepName);
      setCodeSnippet(code);
    } else {
      setMessages((prev) => [...prev, `âŒ ${res.message}`]);
    }
  };

  const selectTable = async (tableName) => {
    try {
      const html = await fetchTableHTML(tableName);
      setSelectedTableHTML(html);
      setMessages((prev) => [...prev, `ðŸ“Š Showing table: ${tableName}`]);
    } catch (err) {
      setMessages((prev) => [...prev, `âŒ Error loading table: ${tableName}`]);
    }
  };

  const resetWorkflow = () => {
    setWorkflowSteps([]);
    setCodeSnippet("");
    setMessages(["ðŸ”„ Workflow reset."]);
    setSelectedTableHTML("");
  };

  return (
    <StateContext.Provider
      value={{
        workflowSteps,
        codeSnippet,
        messages,
        normalizedTables,
        selectedTableHTML,
        addStep,
        selectTable,
        resetWorkflow,
      }}
    >
      {children}
    </StateContext.Provider>
  );
};

export const useStateContext = () => useContext(StateContext);
