import React from "react";
import { useStateContext } from "../context/StateContext";

export default function CodePanel() {
  const { codeForStep, isLoading } = useStateContext();

  return (
    <div
      style={{
        border: "1px solid #ccc",
        padding: 10,
        whiteSpace: "pre-wrap",
        backgroundColor: "#f9f9f9",
        minHeight: 100,
        marginBottom: 10,
      }}
    >
      <h3>Code Panel</h3>
      {isLoading ? <p>Loading code...</p> : <code>{codeForStep || "Select a workflow step"}</code>}
    </div>
  );
}
