import React, { useEffect, useState, useRef } from 'react';
import { useStateContext } from '../context/StateContext';
import DraggableStep from './DraggableStep';
import axios from 'axios';

const STORAGE_KEY = 'workflowStepPositions';

const OutputPanel = () => {
  const { workflowSteps, setCurrentCode } = useStateContext();
  const [positions, setPositions] = useState({});
  const [scale, setScale] = useState(1);
  const containerRef = useRef(null);

  // Store breakpoints: { "stepA-stepB": [{x, y}, ...] }
  const [breakPointsMap, setBreakPointsMap] = useState({});

  const positionsInitialized = useRef(false);

  const boxWidth = 140;
  const boxHeight = 44;
  const horizontalSpacing = 60;
  const verticalSpacing = 60;

  useEffect(() => {
    const savedPositions = localStorage.getItem(STORAGE_KEY);
    if (savedPositions) {
      try {
        const parsedPositions = JSON.parse(savedPositions);
        setPositions(parsedPositions);
        positionsInitialized.current = true;
      } catch {
        console.warn('Failed to parse saved positions');
      }
    }
  }, []);

  useEffect(() => {
    if (positionsInitialized.current) return;
    if (!containerRef.current) return;

    const containerWidth = containerRef.current.clientWidth;
    const initialPositions = {};

    let x = 50;
    let y = 60;

    workflowSteps.forEach((step) => {
      if (x + boxWidth > containerWidth - 20) {
        x = 50;
        y += boxHeight + verticalSpacing;
      }
      initialPositions[step] = { x, y };
      x += boxWidth + horizontalSpacing;
    });

    setPositions((prev) => ({ ...prev, ...initialPositions }));
    positionsInitialized.current = true;
  }, [workflowSteps]);

  useEffect(() => {
    if (positionsInitialized.current) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(positions));
    }
  }, [positions]);

  const handlePositionChange = (step, pos) => {
    setPositions((prev) => ({ ...prev, [step]: pos }));
  };

  const handleClickStep = async (step) => {
    try {
      const response = await axios.get(`/api/code/${step}`);
      setCurrentCode(response.data.code || '');
    } catch {
      setCurrentCode('Failed to load code');
    }
  };

  // Calculate point on cubic Bezier curve at t
  const cubicBezierPoint = (p0, c1, c2, p1, t) => {
    const x =
      Math.pow(1 - t, 3) * p0.x +
      3 * Math.pow(1 - t, 2) * t * c1.x +
      3 * (1 - t) * Math.pow(t, 2) * c2.x +
      Math.pow(t, 3) * p1.x;
    const y =
      Math.pow(1 - t, 3) * p0.y +
      3 * Math.pow(1 - t, 2) * t * c1.y +
      3 * (1 - t) * Math.pow(t, 2) * c2.y +
      Math.pow(t, 3) * p1.y;
    return { x, y };
  };

  // Drag state refs
  const draggingRef = useRef(null);
  const dragSegmentIndexRef = useRef(null);
  const dragTRef = useRef(null);
  const dragLineKeyRef = useRef(null);

  // On mouse down on a curve dot
  const onCurveDotMouseDown = (lineKey, segmentIndex, t, e) => {
    e.preventDefault();
    draggingRef.current = true;
    dragSegmentIndexRef.current = segmentIndex;
    dragTRef.current = t;
    dragLineKeyRef.current = lineKey;
    window.addEventListener('mousemove', onCurveDotMouseMove);
    window.addEventListener('mouseup', onCurveDotMouseUp);
  };

  // On mouse move - update the position of breakpoint near this segment and t
  const onCurveDotMouseMove = (e) => {
    if (!draggingRef.current) return;

    const svg = e.target.ownerSVGElement;
    if (!svg) return;

    const pt = svg.createSVGPoint();
    pt.x = e.clientX;
    pt.y = e.clientY;

    const cursorpt = pt.matrixTransform(svg.getScreenCTM().inverse());

    const lineKey = dragLineKeyRef.current;
    const segmentIndex = dragSegmentIndexRef.current;
    const t = dragTRef.current;

    setBreakPointsMap((prev) => {
      const newBreakPointsMap = { ...prev };
      const breakPoints = newBreakPointsMap[lineKey] ? [...newBreakPointsMap[lineKey]] : [];

      // Find closest breakpoint to segmentIndex, or insert new breakpoint for this segment
      // Strategy: For segmentIndex, the breakpoint index is segmentIndex (breakpoints between points)
      // We'll move that breakpoint or create if none

      if (segmentIndex < breakPoints.length) {
        // Update existing breakpoint position
        breakPoints[segmentIndex] = { x: cursorpt.x / scale, y: cursorpt.y / scale };
      } else {
        // Add breakpoint at this position
        breakPoints.push({ x: cursorpt.x / scale, y: cursorpt.y / scale });
      }

      newBreakPointsMap[lineKey] = breakPoints;
      return newBreakPointsMap;
    });
  };

  const onCurveDotMouseUp = () => {
    draggingRef.current = false;
    dragSegmentIndexRef.current = null;
    dragTRef.current = null;
    dragLineKeyRef.current = null;
    window.removeEventListener('mousemove', onCurveDotMouseMove);
    window.removeEventListener('mouseup', onCurveDotMouseUp);
  };

  // Render SVG paths and draggable dots on curves
  const renderLines = () => {
    const dotRadius = 4;
    const curveDotRadius = 6;
    const containerWidth = containerRef.current ? containerRef.current.clientWidth : 1000;

    const rightEdgeThreshold = containerWidth - 45;
    const leftEdgeThreshold = 20;

    const lines = [];

    for (let i = 0; i < workflowSteps.length - 1; i++) {
      const from = positions[workflowSteps[i]];
      const to = positions[workflowSteps[i + 1]];
      if (!from || !to) continue;

      const fromNearRightEdge = from.x + boxWidth > rightEdgeThreshold;
      const fromNearLeftEdge = from.x < leftEdgeThreshold;
      const toNearRightEdge = to.x + boxWidth > rightEdgeThreshold;
      const toNearLeftEdge = to.x < leftEdgeThreshold;

      // Outgoing start point
      let startX;
      if (fromNearRightEdge) startX = from.x - dotRadius;
      else if (fromNearLeftEdge) startX = from.x + boxWidth + dotRadius;
      else startX = from.x + boxWidth + dotRadius;
      const startY = from.y + boxHeight / 2;

      // Incoming end point
      let endX;
      if (toNearRightEdge) endX = to.x + boxWidth + dotRadius;
      else if (toNearLeftEdge) endX = to.x - dotRadius;
      else endX = to.x - dotRadius;
      const endY = to.y + boxHeight / 2;

      // Breakpoints for this line
      const key = `${workflowSteps[i]}-${workflowSteps[i + 1]}`;
      const breakPoints = breakPointsMap[key] || [];

      // Full points array: start + breakpoints + end
      const points = [{ x: startX, y: startY }, ...breakPoints, { x: endX, y: endY }];

      const pathParts = [`M${points[0].x},${points[0].y}`];

      // Helper for control points for smooth curves
      const getControlPoints = (p0, p1, p2) => {
        const dx1 = p1.x - p0.x;
        const dy1 = p1.y - p0.y;
        const dx2 = p2.x - p1.x;
        const dy2 = p2.y - p1.y;

        return [
          { x: p1.x - dx1 / 3, y: p1.y - dy1 / 3 },
          { x: p1.x + dx2 / 3, y: p1.y + dy2 / 3 },
        ];
      };

      // Build path and collect dots info
      const curveDots = [];

      for (let j = 0; j < points.length - 1; j++) {
        const p0 = j === 0 ? points[j] : points[j - 1];
        const p1 = points[j];
        const p2 = points[j + 1];

        let c1, c2;
        if (j === 0) {
          c1 = { x: (p1.x + p2.x) / 2, y: p1.y };
          c2 = { x: (p1.x + p2.x) / 2, y: p2.y };
        } else {
          [c1, c2] = getControlPoints(p0, p1, p2);
        }

        pathParts.push(`C${c1.x},${c1.y} ${c2.x},${c2.y} ${p2.x},${p2.y}`);

        // Calculate dots on curve segment at t=0.25,0.5,0.75
        [0.25, 0.5, 0.75].forEach((t) => {
          const pt = cubicBezierPoint(p1, c1, c2, p2, t);
          curveDots.push({ ...pt, segmentIndex: j, t });
        });
      }

      const d = pathParts.join(' ');

      // Render the curve path
      lines.push(
        <path
          key={`line-${i}`}
          d={d}
          fill="none"
          stroke="rgba(184, 212, 249, 0.9)"
          strokeWidth="1.5"
          markerEnd="url(#arrowhead)"
        />
      );

      // Render draggable breakpoints as bigger circles
      breakPoints.forEach((pt, idx) => {
        lines.push(
          <circle
            key={`breakpoint-${i}-${idx}`}
            cx={pt.x}
            cy={pt.y}
            r={6}
            fill="rgba(100, 149, 237, 0.7)"
            stroke="rgba(50, 80, 130, 0.9)"
            strokeWidth={1.5}
            style={{ cursor: 'grab' }}
            onMouseDown={(e) => onCurveDotMouseDown(key, idx, 0 /* not used here */, e)}
            onTouchStart={(e) => onCurveDotMouseDown(key, idx, 0, e)}
          />
        );
      });

      // Render draggable dots on curve segments
      curveDots.forEach(({ x, y, segmentIndex, t }, idx) => {
        lines.push(
          <circle
            key={`curve-dot-${i}-${segmentIndex}-${idx}`}
            cx={x}
            cy={y}
            r={5}
            fill="rgba(255, 165, 0, 0.7)"
            stroke="rgba(255, 140, 0, 0.9)"
            strokeWidth={1.2}
            style={{ cursor: 'grab' }}
            onMouseDown={(e) => onCurveDotMouseDown(key, segmentIndex, t, e)}
            onTouchStart={(e) => onCurveDotMouseDown(key, segmentIndex, t, e)}
          />
        );
      });
    }

    return lines;
  };

  return (
    <div
      className="output-panel"
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'auto',
        padding: 20,
      }}
      ref={containerRef}
    >
      <div
        className="zoom-controls"
        style={{ position: 'absolute', top: 5, right: 5, zIndex: 5 }}
      >
        <button
          className="zoom-btn"
          onClick={() => setScale((s) => Math.min(2, +(s + 0.1).toFixed(2)))}
          title="Zoom in"
          style={{ marginRight: 5, textAlign: 'center' }}
        >
          +
        </button>
        <button
          className="zoom-btn"
          onClick={() => setScale((s) => Math.max(0.5, +(s - 0.1).toFixed(2)))}
          title="Zoom out"
        >
          âˆ’
        </button>
      </div>

      <div
        className="canvas-wrapper"
        style={{
          transform: `scale(${scale})`,
          transformOrigin: '0 0',
          position: 'relative',
          minHeight: '100%',
          width: '100%',
        }}
      >
        <svg
          style={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            pointerEvents: 'none',
            overflow: 'visible',
            top: 0,
            left: 0,
          }}
        >
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              fill="rgba(103, 121, 136, 0.9)"
            >
              <polygon points="0 0, 10 3.5, 0 7" />
            </marker>
          </defs>
          {renderLines()}
        </svg>

        {workflowSteps.map((step, index) => (
          <DraggableStep
            key={step}
            step={step}
            position={positions[step] || { x: 0, y: 0 }}
            onPositionChange={handlePositionChange}
            onClick={handleClickStep}
            isFirst={index === 0}
          />
        ))}
      </div>
    </div>
  );
};

export default OutputPanel;
