import React, { useEffect, useRef, useState } from "react";
import mermaid from "mermaid";

const ERDiagramPage = () => {
  const [mermaidCode, setMermaidCode] = useState("");
  const [error, setError] = useState(null);
  const mermaidRef = useRef(null);

  useEffect(() => {
    // Fetch the Mermaid code from backend API
    fetch("http://localhost:5000/api/er_diagram")
      .then(async (res) => {
        if (!res.ok) {
          const text = await res.text();
          throw new Error(`Server Error ${res.status}: ${text}`);
        }
        return res.json();
      })
      .then((data) => {
        if (data.mermaid_code) {
          setMermaidCode(data.mermaid_code);
        } else if (data.error) {
          throw new Error(data.error);
        } else {
          throw new Error("No Mermaid code returned");
        }
      })
      .catch((e) => setError(e.message));
  }, []);

  useEffect(() => {
    if (mermaidCode) {
      try {
        // Initialize Mermaid without automatic rendering
        mermaid.initialize({ startOnLoad: false });

        // Render Mermaid diagram and inject SVG into container
        mermaid.render(
          "mermaidDiagram",
          mermaidCode,
          (svgCode) => {
            if (mermaidRef.current) {
              mermaidRef.current.innerHTML = svgCode;
            }
          }
        );
      } catch (e) {
        setError("Mermaid render error: " + e.message);
      }
    }
  }, [mermaidCode]);

  if (error)
    return <div style={{ color: "red", padding: "1rem" }}>Error: {error}</div>;

  if (!mermaidCode)
    return <div style={{ padding: "1rem" }}>Loading ER Diagram...</div>;

  return (
    <div style={{ padding: "1rem" }}>
      <h2>ER Diagram</h2>
      <div ref={mermaidRef} />
    </div>
  );
};

export default ERDiagramPage;
