import React, { useEffect } from "react";
import { useStateContext } from "../context/StateContext";
import { useNavigate } from "react-router-dom";

const MessagePanel = () => {
  const {
    messages,
    normalizedTables,
    fetchNormalizedTables,
    setSelectedTable,
  } = useStateContext();
  const navigate = useNavigate();

  useEffect(() => {
    fetchNormalizedTables();
    
  }, [fetchNormalizedTables]);

  const handleTableClick = (tableName) => {
    setSelectedTable(tableName);
    navigate("/table-view");
  };

  return (
    <div className="message-panel" style={{ overflowY: "auto", height: "100%", padding: 10 }}>
      <h2>Messages</h2>
      <div style={{ maxHeight: 150, overflowY: "auto", border: "1px solid #ddd", padding: 5, marginBottom: 10 }}>
        {messages.length === 0 && <p>No messages yet</p>}
        {messages.map((msg, idx) => (
          <p key={idx} style={{ margin: 2, fontSize: 14 }}>
            {msg}
          </p>
        ))}
      </div>

      <h3>Normalized Tables</h3>
      <div>
        {normalizedTables.length === 0 ? (
          <p>No tables available</p>
        ) : (
          normalizedTables.map((table) => (
            <button
              key={table}
              onClick={() => handleTableClick(table)}
              style={{ display: "block", margin: "5px 0", width: "100%" }}
            >
              {table}
            </button>
          ))
        )}
      </div>
    </div>
  );
};

export default MessagePanel;
