import React, { useEffect, useRef } from 'react';
import { useDrag } from '@use-gesture/react';

const DraggableStep = ({ step, displayName, position, onPositionChange, onClick, isFirst, onSizeChange }) => {
  const ref = useRef(null);

  useEffect(() => {
    if (ref.current) {
      const width = ref.current.offsetWidth;
      onSizeChange(step, width);
    }
  }, [displayName, onSizeChange, step]);

  // Gesture for dragging
  const bind = useDrag(({ offset: [x, y] }) => {
    onPositionChange(step, { x, y });
  }, {
    from: () => [position.x, position.y]
  });

  return (
    <div
      {...bind()}
      ref={ref}
      onClick={() => onClick(step)}

      title={displayName}
    >
      {displayName}
    </div>
  );
};

export default DraggableStep;
