import React, { useEffect, useState } from 'react';
import MermaidERDiagram from './MermaidERDiagram';

const ERDiagramPage = () => {
  const [mermaidCode, setMermaidCode] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('/api/er_diagram')
      .then((res) => res.json())
      .then((data) => {
        if (data.mermaid_code) {
          setMermaidCode(data.mermaid_code);
          setError(null);
        } else {
          setError(data.error || 'No Mermaid ER diagram data received.');
        }
      })
      .catch((err) => {
        setError(err.message || 'Failed to fetch ER diagram');
      })
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <div>Loading ER Diagram...</div>;
  if (error) return <div style={{ color: 'red' }}>Error: {error}</div>;

  return (
    <div>
      <h2>Entity-Relationship Diagram</h2>
      <MermaidERDiagram mermaidCode={mermaidCode} />
    </div>
  );
};

export default ERDiagramPage;
